(10, 'Ban lãnh đạo', '/public/images/gioi%20thieu%20-%20ban%20lanh%20dao.jpg', '', '&lt;p&gt;&lt;img alt=&quot;&quot; src=&quot;/public/images/gioi%20thieu%20-%20ban%20lanh%20dao.jpg&quot; style=&quot;width:100%&quot; /&gt;&lt;/p&gt;\r\n', '', '', '', 'gioi-thieu/ban-lanh-dao', 2, 0, 1, 1, '2015-09-17 18:16:41', '2015-09-17 18:16:41', 1, 1, 1),
(11, 'Thư ngỏ', '/public/images/tintuc/thungo.jpg', '', '&lt;p&gt;&lt;img alt=&quot;&quot; src=&quot;/public/images/tintuc/thungo.jpg&quot; style=&quot;height:1200px; width:848px&quot; /&gt;&lt;/p&gt;\n', '', '', '', 'gioi-thieu/thu-ngo', 2, 0, 1, 0, '2015-09-17 18:25:40', '2015-10-26 03:41:19', 1, 0, 0),
(12, 'Tầm nhìn', '/public/images/iphone_6_review_hero.jpg', '&lt;p&gt;&lt;span style=&quot;font-size:18px&quot;&gt;&lt;span style=&quot;font-family:times new roman,times,serif&quot;&gt;&lt;strong&gt;Tầm Nh&amp;igrave;n:&lt;/strong&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\n', '&lt;ul style=&quot;list-style-type:circle&quot;&gt;\n	&lt;li&gt;X&amp;acirc;y dựng v&amp;agrave; ph&amp;aacute;t tri&amp;ecirc;̉n thương hi&amp;ecirc;̣u SVG th&amp;agrave;nh thương hi&amp;ecirc;̣u b&amp;ecirc;̀n vững, đẳng c&amp;acirc;́p d&amp;acirc;̃n đ&amp;acirc;̀u trong c&amp;aacute;c c&amp;ocirc;ng ty b&amp;aacute;n h&amp;agrave;ng trực ti&amp;ecirc;́p tại Vi&amp;ecirc;̣t Nam.&lt;/li&gt;\n	&lt;li&gt;Tạo dựng c&amp;ocirc;̣ng đ&amp;ocirc;̀ng SVG s&amp;ocirc;́ng khỏe, th&amp;agrave;nh c&amp;ocirc;ng v&amp;agrave; hạnh ph&amp;uacute;c th&amp;ocirc;ng qua những d&amp;ograve;ng sản ph&amp;acirc;̉m ch&amp;acirc;́t lượng ti&amp;ecirc;u chuẩn quốc tế, đ&amp;ocirc;̣c đ&amp;aacute;o, đ&amp;ocirc;̣c quy&amp;ecirc;̀n mang đ&amp;acirc;̣m n&amp;eacute;t tr&amp;iacute; tu&amp;ecirc;̣ Vi&amp;ecirc;̣t, bản sắc vi&amp;ecirc;̣t, con người Vi&amp;ecirc;̣t.&lt;/li&gt;\n&lt;/ul&gt;\n', 'Tầm nhìn và Sứ mệnh', '', '', 'tam-nhin', 2, 0, 1, 2, '2015-09-17 18:35:07', '2015-10-17 06:48:06', 1, 0, 0),
(15, 'Lịch hoạt động NPP', '/public/images/60_Polka_Dots_Beach_Backpack-254x402.jpg', '&lt;p&gt;Lorem ipsum dolor sit amet, consectetur adipiscing elit. Donec fringilla nisl id nulla varius placerat ac eu diam. Fusce ornare mi ut elit rhoncus tincidunt. Suspendisse semper, orci at convallis luctus, sem ex mattis sapien, sed lobortis elit quam sit amet turpis. Sed sit amet orci et ipsum placerat cursus. Vivamus quis mi porttitor, commodo lectus non, vulputate dui. Praesent porta aliquam dui, sed porta est tempor ut. Morbi lobortis, mi ac convallis imperdiet, eros nibh viverra dui, mollis dapibus turpis&lt;/p&gt;\n', '&lt;p&gt;Lorem ipsum dolor sit amet, consectetur adipiscing elit. Donec fringilla nisl id nulla varius placerat ac eu diam. Fusce ornare mi ut elit rhoncus tincidunt. Suspendisse semper, orci at convallis luctus, sem ex mattis sapien, sed lobortis elit quam sit amet turpis. Sed sit amet orci et ipsum placerat cursus. Vivamus quis mi porttitor, commodo lectus non, vulputate dui. Praesent porta aliquam dui, sed porta est tempor ut. Morbi lobortis, mi ac convallis imperdiet, eros nibh viverra dui, mollis dapibus turpis&lt;/p&gt;\n', '', '', '', 'thong-tin-npp/lich-hoat-dong-npp', 16, 0, 1, 0, '2015-09-19 06:33:31', '2015-09-19 06:36:25', 1, 1, 1),
(16, 'Lợi và hại của thực phẩm chức năng', '/public/images/Functional-foods-at-the-forefr-5600-7018-1387770506.jpg', '&lt;p&gt;Tuy kh&amp;ocirc;ng được coi l&amp;agrave; thuốc nhưng&amp;nbsp;thực phẩm chức năng cũng c&amp;oacute; khả nảng g&amp;acirc;y c&amp;aacute;c phản ứng dị ứng. Lạm dụng c&amp;oacute; thể&amp;nbsp;l&amp;agrave;m rối loạn qu&amp;aacute; tr&amp;igrave;nh đồng ho&amp;aacute; trao đổi chất do cơ thể nhận dư thừa chất bổ, chất dinh dưỡng.&lt;/p&gt;\n', '&lt;p&gt;Ở Việt Nam thực phẩm chức năng tuy mới xuất hiện gần đ&amp;acirc;y trong sự đ&amp;oacute;n nhận d&amp;egrave; dặt của d&amp;acirc;n ch&amp;uacute;ng nhưng n&amp;oacute; cũng đ&amp;atilde; lan dần như một l&amp;agrave;n s&amp;oacute;ng ngầm rộng khắp. T&amp;acirc;m l&amp;yacute; đ&amp;aacute;m đ&amp;ocirc;ng của người Việt cũng l&amp;agrave;m cho Việt Nam trở th&amp;agrave;nh &amp;quot;miền đất hứa&amp;quot; cho kiểu b&amp;aacute;n h&amp;agrave;ng đa cấp. Những vấn nạn về x&amp;atilde; hội v&amp;agrave; m&amp;ocirc;i trường, &amp;aacute;m ảnh về vệ sinh an to&amp;agrave;n thực phẩm v&amp;agrave; nỗi lo về sức khoẻ bệnh tật khiến cho thực phẩm chức năng trở th&amp;agrave;nh niềm hy vọng, l&amp;agrave; cứu c&amp;aacute;nh cho mỗi người v&amp;agrave; mỗi gia đ&amp;igrave;nh. Tuy nhi&amp;ecirc;n, cần c&amp;oacute; sự hiểu biết đ&amp;uacute;ng đắn về thực phẩm chức năng để c&amp;oacute; th&amp;aacute;i độ h&amp;agrave;nh xử đ&amp;uacute;ng đắn, kh&amp;ocirc;ng b&amp;agrave;i x&amp;iacute;ch v&amp;agrave; cũng kh&amp;ocirc;ng m&amp;ecirc; muội.&lt;/p&gt;\n\n&lt;p&gt;&lt;img alt=&quot;hinh ảnh minh họa&quot; src=&quot;/public/images/thuc-pham-chuc-nang.jpg&quot; style=&quot;float:left; height:227px; width:300px&quot; /&gt;&lt;/p&gt;\n\n&lt;p&gt;Theo định nghĩa của Bộ Y tế, thực phẩm chức năng&amp;nbsp;l&amp;agrave; thực phẩm d&amp;ugrave;ng để hỗ trợ chức năng những cơ quan bộ phận trong cơ thể người, c&amp;oacute; t&amp;aacute;c dụng dinh dưỡng, tạo cho cơ thể t&amp;igrave;nh trạng thoải m&amp;aacute;i, tăng sức đề kh&amp;aacute;ng, giảm bớt nguy cơ g&amp;acirc;y bệnh.&lt;/p&gt;\n\n&lt;p&gt;Bản th&amp;acirc;n thực phẩm thi&amp;ecirc;n nhi&amp;ecirc;n đ&amp;atilde; cung cấp đầy đủ chất dinh dưỡng v&amp;agrave; những chất c&amp;oacute; lợi cho chức năng cơ thể, ph&amp;ograve;ng ngừa bệnh tật, thậm ch&amp;iacute; c&amp;oacute; khả năng chữa bệnh. Đ&amp;acirc;y l&amp;agrave; kiến thức m&amp;agrave; từ xa xưa lo&amp;agrave;i người đ&amp;atilde; biết.&amp;nbsp;Tuy nhi&amp;ecirc;n, do nhiều l&amp;yacute; do như tập tục th&amp;oacute;i quen, khẩu vị, m&amp;ocirc;i trường sống, khả năng kinh tế, con người kh&amp;ocirc;ng biết hoặc kh&amp;ocirc;ng c&amp;oacute; điều kiện để ăn uống một c&amp;aacute;ch đầy đủ v&amp;agrave; c&amp;acirc;n đối nguồn thực phẩm thi&amp;ecirc;n nhi&amp;ecirc;n. V&amp;igrave; vậy việc bổ sung đ&amp;uacute;ng c&amp;aacute;ch c&amp;aacute;c chất c&amp;ograve;n thiếu để chế độ dinh dưỡng trở n&amp;ecirc;n đầy đủ v&amp;agrave; c&amp;acirc;n bằng l&amp;agrave; rất cần thiết.&lt;/p&gt;\n\n&lt;p&gt;&lt;strong&gt;Lợi &amp;iacute;ch của thực phẩm chức năng&lt;/strong&gt;&lt;/p&gt;\n\n&lt;p&gt;&lt;strong&gt;&lt;em&gt;-&amp;nbsp;&lt;/em&gt;&lt;/strong&gt;Bổ sung nhanh ch&amp;oacute;ng chất dinh dưỡng v&amp;agrave; c&amp;aacute;c chất c&amp;oacute; t&amp;aacute;c dụng chức năng m&amp;agrave; cơ thể kh&amp;ocirc;ng được cung cấp đầy đủ trong chế độ ăn uống h&amp;agrave;ng ng&amp;agrave;y.&lt;/p&gt;\n\n&lt;p&gt;-&amp;nbsp;C&amp;oacute; thể tạm thời thay thế bữa ăn khi kh&amp;ocirc;ng c&amp;oacute; điều kiện ăn uống b&amp;igrave;nh thường (như khi ở m&amp;ocirc;i trường thiếu thốn thực phẩm hoặc kh&amp;ocirc;ng thể ăn được v&amp;igrave; l&amp;yacute; do b&amp;ecirc;nh tật).&lt;/p&gt;\n\n&lt;p&gt;-&amp;nbsp;C&amp;aacute;c chế phẩm đều ở dạng tinh chế rất tiện lợi, dễ sử dụng v&amp;agrave; bảo quản.&lt;/p&gt;\n\n&lt;p&gt;-&amp;nbsp;C&amp;oacute; nhiều sản phẩm để chọn lựa ph&amp;ugrave; hợp với t&amp;igrave;nh trạng cơ thể từng người.&lt;/p&gt;\n\n&lt;p&gt;-&amp;nbsp;Mua v&amp;agrave; d&amp;ugrave;ng dễ d&amp;agrave;ng kh&amp;ocirc;ng cần phải c&amp;oacute; thầy thuốc kh&amp;aacute;m bệnh k&amp;ecirc; toa.&lt;/p&gt;\n\n&lt;p&gt;-&amp;nbsp;Khi sử dụng thực phẩm chức năng, người sử dụng sẽ c&amp;oacute; &amp;yacute; thức chăm lo cho sức khoẻ, thay đổi th&amp;oacute;i quen để c&amp;oacute; chế độ ăn uống hợp l&amp;yacute; v&amp;agrave; lối sống l&amp;agrave;nh mạnh c&amp;oacute; lợi cho sức khoẻ hơn.&lt;/p&gt;\n\n&lt;p&gt;-&amp;nbsp;Nguồn cung cấp dồi d&amp;agrave;o thường xuy&amp;ecirc;n, mạng lưới rộng khắp.&lt;/p&gt;\n\n&lt;p&gt;-&amp;nbsp;Người d&amp;ugrave;ng được khuyến kh&amp;iacute;ch d&amp;ugrave;ng theo kiểu b&amp;aacute;n h&amp;agrave;ng đa cấp, mua c&amp;agrave;ng nhiều c&amp;agrave;ng rẻ v&amp;agrave; được hưởng tiền chiết khấu nếu giới thiệu th&amp;ecirc;m được kh&amp;aacute;ch h&amp;agrave;ng n&amp;ecirc;n c&amp;oacute; thể kiếm th&amp;ecirc;m thu nhập.&amp;nbsp;&amp;nbsp;&lt;/p&gt;\n\n&lt;p&gt;&lt;strong&gt;Một số mặt hại&lt;/strong&gt;&lt;/p&gt;\n\n&lt;p&gt;&lt;em&gt;-&amp;nbsp;&lt;/em&gt;C&amp;aacute;c cơ quan quản l&amp;yacute; về y tế v&amp;agrave; sức khoẻ tr&amp;ecirc;n thế giới cũng như Việt Nam đ&amp;atilde; c&amp;oacute; những quy định về việc quản l&amp;yacute; chất lượng thực phẩm chức năng v&amp;agrave; ngăn cấm những quảng c&amp;aacute;o qu&amp;aacute; mức, sai sự thật. C&amp;aacute;c sản phẩm thực phẩm chức năng với những lời ca ngợi c&amp;oacute; c&amp;aacute;nh, kiểu giới thiệu lập lờ khiến người ti&amp;ecirc;u d&amp;ugrave;ng kh&amp;oacute; c&amp;oacute; thể c&amp;oacute; sự chọn lựa đ&amp;uacute;ng đắn cho m&amp;igrave;nh. Ngay với Omega-3, sản phẩm được c&amp;ocirc;ng nhận l&amp;agrave; cần thiết cho ph&amp;aacute;t triển của n&amp;atilde;o, ngăn ngừa c&amp;aacute;c rối loạn mỡ m&amp;aacute;u v&amp;agrave; c&amp;aacute;c bệnh tim mạch, nhưng nh&amp;agrave; sản xuất vẫn bị cơ quan quản l&amp;yacute; Thuốc v&amp;agrave; Thực phẩm Mỹ bắt ngừng quảng c&amp;aacute;o v&amp;igrave; d&amp;ugrave;ng chữ &amp;quot;ph&amp;aacute;t triển tr&amp;iacute; n&amp;atilde;o&amp;quot;.&lt;/p&gt;\n\n&lt;p&gt;-&amp;nbsp;C&amp;aacute;ch cung cấp kiểu b&amp;aacute;n h&amp;agrave;ng đa cấp&amp;nbsp;với mục đ&amp;iacute;ch l&amp;ocirc;i k&amp;eacute;o người mua bằng mọi gi&amp;aacute; để kiếm lợi&amp;nbsp;dễ mang đến những th&amp;ocirc;ng tin thiếu ch&amp;iacute;nh x&amp;aacute;c v&amp;agrave; ngộ nhận. Nhất l&amp;agrave; với những &amp;quot;tiếp thị vi&amp;ecirc;n&amp;quot; &amp;nbsp;kh&amp;ocirc;ng c&amp;oacute; kiến thức y học, thậm ch&amp;iacute; thiếu kiến thức văn ho&amp;aacute; th&amp;ocirc;ng thường th&amp;igrave; sự truyền đạt giới thiệu dễ thi&amp;ecirc;n lệch v&amp;agrave; sai s&amp;oacute;t.&amp;nbsp;&lt;/p&gt;\n\n&lt;p&gt;-&amp;nbsp;Những hiểu biết kh&amp;ocirc;ng đ&amp;uacute;ng đắn c&amp;oacute; thể khiến người d&amp;ugrave;ng thực phẩm chức năng bỏ qu&amp;ecirc;n bữa ăn th&amp;ocirc;ng thường, lấy thực phẩm chức năng thay thế cho nguồn dinh dưỡng từ những thực phẩm tự nhi&amp;ecirc;n. Nghĩa l&amp;agrave; dẫn từ chế độ ăn chưa hợp l&amp;yacute; một c&amp;aacute;ch v&amp;ocirc; t&amp;igrave;nh đến chế độ ăn mất c&amp;acirc;n đối một c&amp;aacute;ch chủ &amp;yacute;.&lt;/p&gt;\n\n&lt;p&gt;-&amp;nbsp;Sự nguy hại c&amp;ograve;n lớn hơn nếu người ti&amp;ecirc;u d&amp;ugrave;ng lấy thực phẩm chức năng thay thế thuốc chữa bệnh. Sự ngộ nhận n&amp;agrave;y c&amp;oacute; nguy&amp;ecirc;n nh&amp;acirc;n từ những quảng c&amp;aacute;o thổi phồng qu&amp;aacute; mức v&amp;agrave; những chế phẩm thực phẩm chức năng được sản xuất v&amp;agrave; đ&amp;oacute;ng g&amp;oacute;i tương tự như c&amp;aacute;c dược phẩm. Mặc d&amp;ugrave; Bộ Y tế đ&amp;atilde; cấm k&amp;ecirc; toa v&amp;agrave; cấm quảng c&amp;aacute;o sử dụng thực phẩm chức năng thay thuốc điều trị nhưng việc loại trừ hết những sai s&amp;oacute;t n&amp;agrave;y l&amp;agrave; hết sức kh&amp;oacute; khăn.&lt;/p&gt;\n\n&lt;p&gt;-&amp;nbsp;Mặt kh&amp;aacute;c nếu hiểu thực phẩm chức năng l&amp;agrave; v&amp;ocirc; hại rồi d&amp;ugrave;ng qu&amp;aacute; nhiều, d&amp;ugrave;ng k&amp;eacute;o d&amp;agrave;i li&amp;ecirc;n tục nhiều th&amp;aacute;ng nhiều năm th&amp;igrave; ngo&amp;agrave;i việc tốn k&amp;eacute;m tiền bạc c&amp;ograve;n mang lại nhiều t&amp;aacute;c hại kh&amp;ocirc;ng nhỏ. T&amp;aacute;c hại trước hết l&amp;agrave; l&amp;agrave;m rối loạn qu&amp;aacute; tr&amp;igrave;nh đồng ho&amp;aacute; trao đổi chất trong cơ thể do li&amp;ecirc;n tục phải tiếp nhận dư thừa nhiều loại chất bổ v&amp;agrave; chất dinh dưỡng. Năng lượng dư thừa được kết hợp với c&amp;aacute;c chất dư thừa kh&amp;aacute;c để dự trữ trong cơ thể l&amp;agrave;m mỡ m&amp;aacute;u tăng cao v&amp;agrave; m&amp;ocirc; mỡ dự trữ cũng ph&amp;igrave; đại, đường huyết tăng v&amp;agrave; c&amp;aacute;c sản phẩm oxy ho&amp;aacute; cũng tăng theo, g&amp;acirc;y t&amp;aacute;c hại đến nhiều cơ quan bộ phận, đẩy nhanh qu&amp;aacute; tr&amp;igrave;nh l&amp;atilde;o ho&amp;aacute; của cơ thể.&lt;/p&gt;\n\n&lt;p&gt;-&amp;nbsp;Tuy kh&amp;ocirc;ng được coi l&amp;agrave; thuốc nhưng&amp;nbsp;thực phẩm chức năng cũng c&amp;oacute; khả nảng g&amp;acirc;y c&amp;aacute;c phản ứng dị ứng cho người d&amp;ugrave;ng từ nhẹ đến nặng, thậm ch&amp;iacute; g&amp;acirc;y sốc phản vệ nguy hiểm đến t&amp;iacute;nh mạng.&lt;/p&gt;\n\n&lt;p&gt;-&amp;nbsp;Qu&amp;aacute; m&amp;ecirc; t&amp;iacute;n thực phẩm chức năng m&amp;agrave; coi nhẹ việc thay đổi th&amp;oacute;i quen ăn uống, cải thiện điều kiện sống v&amp;agrave; lối sống th&amp;igrave; kh&amp;ocirc;ng những kh&amp;ocirc;ng ngăn ngừa được nguy cơ bệnh tật m&amp;agrave; cũng kh&amp;ocirc;ng thể duy tr&amp;igrave; được t&amp;igrave;nh trạng sức khoẻ cần thiết cho một cuộc sống b&amp;igrave;nh thường.&lt;/p&gt;\n\n&lt;p&gt;Thực phẩm chức năng d&amp;ugrave; tốt đến đ&amp;acirc;u cũng chỉ l&amp;agrave; một trong những nguồn bổ sung c&amp;aacute;c chất cần thiết cho cơ thể. N&amp;oacute; kh&amp;ocirc;ng thể thay thế được những nguồn dinh dưỡng tự nhi&amp;ecirc;n. Muốn khoẻ mạnh phải c&amp;oacute; những giải ph&amp;aacute;p đồng bộ v&amp;agrave; to&amp;agrave;n diện. Phải duy tr&amp;igrave; một chế độ ăn uống hợp l&amp;yacute;, x&amp;acirc;y dựng một lối sống l&amp;agrave;nh mạnh khoa học để đạt tới một thể chất lu&amp;ocirc;n lu&amp;ocirc;n sung m&amp;atilde;n v&amp;agrave; một trạng th&amp;aacute;i tinh thần lu&amp;ocirc;n thanh thản v&amp;agrave; lạc quan. Lu&amp;ocirc;n lu&amp;ocirc;n phải như vậy để chủ động bảo vệ sức khoẻ bản th&amp;acirc;n v&amp;agrave; kh&amp;ocirc;ng bị tốn tiền v&amp;ocirc; &amp;iacute;ch v&amp;igrave; thiếu hiểu biết.&lt;/p&gt;\n\n&lt;p&gt;B&amp;aacute;c sĩ&lt;strong&gt;&amp;nbsp;Cao Ngọc B&amp;iacute;ch&lt;/strong&gt;&lt;/p&gt;\n', '', '', '', 'loi-va-hai-cua-thuc-pham-chuc-nang', 13, 0, 0, 0, '2015-09-21 17:02:57', '2015-09-21 17:08:28', 1, 1, 1),
(17, 'Thực phẩm chức năng là gì?', '/public/images/Functional-foods-at-the-forefr-5600-7018-1387770506.jpg', '&lt;p&gt;&lt;strong&gt;Thực phẩm chức năng&lt;/strong&gt;&amp;nbsp;(&lt;a href=&quot;https://vi.wikipedia.org/wiki/Ti%E1%BA%BFng_Anh&quot;&gt;tiếng Anh&lt;/a&gt;:&amp;nbsp;&lt;em&gt;functional foods&lt;/em&gt;) l&amp;agrave; c&amp;aacute;c sản phẩm c&amp;oacute; nguồn gốc tự nhi&amp;ecirc;n hoặc l&amp;agrave; thực phẩm trong qu&amp;aacute; tr&amp;igrave;nh chế biến được bổ sung th&amp;ecirc;m c&amp;aacute;c chất &amp;quot;chức năng&amp;quot;.&lt;a href=&quot;https://vi.wikipedia.org/wiki/Th%E1%BB%B1c_ph%E1%BA%A9m_ch%E1%BB%A9c_n%C4%83ng#cite_note-1&quot;&gt;[1]&lt;/a&gt;&amp;nbsp;Cũng như thực phẩm thuốc, thực phẩm chức năng nằm ở nơi giao thoa giữa thực phẩm v&amp;agrave; thuốc v&amp;agrave; người ta cũng gọi thực phẩm chức năng l&amp;agrave; thực phẩm thuốc. Sở dĩ&amp;nbsp;&lt;strong&gt;thực phẩm chức năng&lt;/strong&gt;&amp;nbsp;c&amp;oacute; khả năng hỗ trợ điều trị bệnh v&amp;agrave; ph&amp;ograve;ng ngừa bệnh v&amp;igrave; n&amp;oacute; c&amp;oacute; khả năng phục hồi tất cả c&amp;aacute;c cấu tr&amp;uacute;c tế b&amp;agrave;o trong cơ thể đang bị tổn thương.&lt;/p&gt;\r\n', '&lt;h1&gt;1. KH&amp;Aacute;I NIỆM&lt;/h1&gt;\r\n\r\n&lt;p&gt;&lt;a href=&quot;https://vi.wikipedia.org/w/index.php?title=B%E1%BB%99_Y_t%E1%BA%BF&amp;amp;action=edit&amp;amp;redlink=1&quot;&gt;Bộ Y tế&lt;/a&gt;&amp;nbsp;Việt Nam định nghĩa thực phẩm chức năng: l&amp;agrave; thực phẩm d&amp;ugrave;ng để hỗ trợ chức năng của c&amp;aacute;c bộ phận trong cơ thể người, c&amp;oacute; t&amp;aacute;c dụng dinh dưỡng, tạo cho cơ thể t&amp;igrave;nh trạng thoải m&amp;aacute;i, tăng sức đề kh&amp;aacute;ng v&amp;agrave; giảm bớt nguy cơ g&amp;acirc;y bệnh. Tuỳ theo c&amp;ocirc;ng thức, h&amp;agrave;m lượng vi chất v&amp;agrave; hướng dẫn sử dụng, thực phẩm chức năng c&amp;ograve;n c&amp;oacute; c&amp;aacute;c t&amp;ecirc;n gọi sau: thực phẩm bổ sung vi chất dinh dưỡng, thực phẩm bổ sung, thực phẩm bảo vệ sức khoẻ, sản phẩm dinh dưỡng y học.&lt;/p&gt;\r\n\r\n&lt;p&gt;Ở mỗi nước, thực phẩm chức năng (TPCN) được gọi bằng những thuật ngữ kh&amp;aacute;c nhau: C&amp;aacute;c nước T&amp;acirc;y &amp;Acirc;u gọi l&amp;agrave; &amp;quot;thực phẩm - thuốc&amp;quot; (alicaments) hoặc dược phẩm dinh dưỡng (nutraceutics), thực phẩm bổ sung dinh dưỡng (food suplement); Trung Quốc gọi l&amp;agrave; &amp;quot;thực phẩm bổ dưỡng bảo vệ sức khỏe&amp;quot;; Việt Nam gọi l&amp;agrave; &amp;quot;thực phẩm đặc biệt&amp;quot;.&lt;/p&gt;\r\n\r\n&lt;h1&gt;2. PH&amp;Acirc;N BIỆT THỰC PHẨM CHỨC NĂNG&lt;/h1&gt;\r\n\r\n&lt;h2&gt;Thực phẩm chức năng kh&amp;aacute;c với thực phẩm&lt;/h2&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li&gt;Được sản xuất, chế biến theo c&amp;ocirc;ng thức: bổ sung một số th&amp;agrave;nh phần c&amp;oacute; lợi hoặc loại bớt một số th&amp;agrave;nh phần bất lợi (để ki&amp;ecirc;ng). Việc bổ sung hay loại bớt phải được chứng minh v&amp;agrave; c&amp;acirc;n nhắc một c&amp;aacute;ch khoa học v&amp;agrave; được cơ quan nh&amp;agrave; nước c&amp;oacute; thẩm quyền cho ph&amp;eacute;p (thường l&amp;agrave; phải theo ti&amp;ecirc;u chuẩn).&lt;/li&gt;\r\n	&lt;li&gt;C&amp;oacute; t&amp;aacute;c dụng với sức khỏe (một số chức năng sinh l&amp;yacute; của cơ thể) nhiều hơn l&amp;agrave; c&amp;aacute;c chất dinh dưỡng th&amp;ocirc;ng thường. Nghĩa l&amp;agrave;, thực phẩm chức năng &amp;iacute;t tạo ra năng lượng cho cơ thể như c&amp;aacute;c loại thực phẩm, v&amp;iacute; dụ, gạo, thịt, c&amp;aacute;&amp;hellip;&lt;/li&gt;\r\n	&lt;li&gt;Liều sử dụng thường nhỏ, thậm ch&amp;iacute; t&amp;iacute;nh bằng gram, miligram như l&amp;agrave; thuốc.&lt;/li&gt;\r\n	&lt;li&gt;Đối tượng sử dụng c&amp;oacute; chỉ định r&amp;otilde; rệt như người gi&amp;agrave;, trẻ em, phụ nữ tuổi m&amp;atilde;n kinh, người c&amp;oacute; hội chứng thiếu vi chất, rối loạn chức năng sinh l&amp;yacute; n&amp;agrave;o đ&amp;oacute;&amp;hellip;&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;h2&gt;Thực phẩm chức năng kh&amp;ocirc;ng phải l&amp;agrave; thuốc&lt;/h2&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li&gt;Đối với thực phẩm chức năng, nh&amp;agrave; sản xuất c&amp;ocirc;ng bố tr&amp;ecirc;n nh&amp;atilde;n sản phẩm l&amp;agrave; thực phẩm, đảm bảo chất lượng vệ sinh an to&amp;agrave;n sức khỏe, ph&amp;ugrave; hợp với c&amp;aacute;c quy định về thực phẩm. Đối với thuốc, nh&amp;agrave; sản xuất c&amp;ocirc;ng bố tr&amp;ecirc;n nh&amp;atilde;n l&amp;agrave; sản phẩm thuốc, c&amp;oacute; t&amp;aacute;c dụng chữa bệnh, ph&amp;ograve;ng bệnh với c&amp;ocirc;ng dụng, chỉ định, liều d&amp;ugrave;ng, chống chỉ định. Thuốc l&amp;agrave; những sản phẩm để điều trị v&amp;agrave; ph&amp;ograve;ng bệnh, được chỉ định để nhằm t&amp;aacute;i lập, điều chỉnh hoặc sửa đổi chức năng sinh l&amp;yacute; của cơ thể.&lt;/li&gt;\r\n	&lt;li&gt;C&amp;oacute; thể sử dụng thường xuy&amp;ecirc;n, l&amp;acirc;u d&amp;agrave;i nhằm nu&amp;ocirc;i dưỡng (thức ăn qua sonde), bổ dưỡng hoặc ph&amp;ograve;ng ngừa c&amp;aacute;c nguy cơ g&amp;acirc;y bệnh&amp;hellip; m&amp;agrave; vẫn an to&amp;agrave;n, kh&amp;ocirc;ng c&amp;oacute; độc hại, kh&amp;ocirc;ng c&amp;oacute; phản ứng phụ.&lt;/li&gt;\r\n	&lt;li&gt;Người ti&amp;ecirc;u d&amp;ugrave;ng c&amp;oacute; thể tự sử dụng theo &amp;quot;hướng dẫn c&amp;aacute;ch sử dụng&amp;quot; của nh&amp;agrave; sản xuất m&amp;agrave; kh&amp;ocirc;ng cần kh&amp;aacute;m bệnh, hoặc thầy thuốc phải k&amp;ecirc; đơn&amp;hellip;&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;h2&gt;C&amp;aacute;c loại thực phẩm chức năng&lt;/h2&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li&gt;Nh&amp;oacute;m bổ sung vitamin v&amp;agrave; kho&amp;aacute;ng chất&lt;/li&gt;\r\n	&lt;li&gt;Nh&amp;oacute;m bổ sung chất xơ&lt;/li&gt;\r\n	&lt;li&gt;Thực phẩm gi&amp;uacute;p c&amp;acirc;n bằng hệ vi khuẩn đường ti&amp;ecirc;u h&amp;oacute;a&lt;/li&gt;\r\n	&lt;li&gt;Bổ sung c&amp;aacute;c chất dinh dưỡng đặc biệt kh&amp;aacute;c&lt;/li&gt;\r\n	&lt;li&gt;Thực phẩm loại bỏ bớt một số th&amp;agrave;nh phần&lt;/li&gt;\r\n	&lt;li&gt;C&amp;aacute;c thực phẩm cho nhu cầu dinh dưỡng đặc biệt&lt;/li&gt;\r\n	&lt;li&gt;Thực phẩm chức năng hỗ trợ giảm c&amp;acirc;n, giảm b&amp;eacute;o&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;h2&gt;&amp;nbsp;C&amp;aacute;c lưu &amp;yacute; khi sử dụng thực phẩm chức năng&lt;/h2&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li&gt;Đối với những thực phẩm ở dạng tự nhi&amp;ecirc;n, việc chọn lựa cần lưu &amp;yacute; chọn lựa những sản phẩm tươi, mới v&amp;agrave; đảm bảo vệ sinh an to&amp;agrave;n thực phẩm như dư lượng thuốc bảo vệ thực vật. Để đạt được những lợi &amp;iacute;ch về sức khỏe, cần sử dụng thường xuy&amp;ecirc;n với số lượng khuyến nghị.&lt;/li&gt;\r\n	&lt;li&gt;Những sản phẩm đ&amp;atilde; qua chế biến, đ&amp;ograve;i hỏi người ti&amp;ecirc;u d&amp;ugrave;ng phải c&amp;oacute; kỹ năng đọc nh&amp;atilde;n bao b&amp;igrave;. Thực phẩm chức năng thường được đ&amp;oacute;ng g&amp;oacute;i giống như thực phẩm th&amp;ocirc;ng thường. Ngo&amp;agrave;i c&amp;aacute;c th&amp;ocirc;ng tin của một thực phẩm th&amp;ocirc;ng thường, thực phẩm chức năng bắt buộc phải ghi r&amp;otilde; t&amp;ecirc;n nh&amp;oacute;m sản phẩm như thực phẩm bổ sung vi chất hay thực phẩm bảo vệ sức khỏe, thực phẩm ăn ki&amp;ecirc;ng, &amp;hellip;.&lt;/li&gt;\r\n	&lt;li&gt;Tr&amp;ecirc;n bao b&amp;igrave; thường cung cấp 2 loại th&amp;ocirc;ng tin:&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;ol&gt;\r\n	&lt;li&gt;X&amp;aacute;c nhận c&amp;oacute; lợi cho sức khỏe (health claim): V&amp;iacute; dụ như sản phẩm d&amp;agrave;nh cho người tiểu đường hoặc sản phẩm d&amp;ugrave;ng để nu&amp;ocirc;i qua ống th&amp;ocirc;ng dạ d&amp;agrave;y. Những thực phẩm c&amp;oacute; x&amp;aacute;c nhận về sức khỏe phải được cơ quan y tế chứng nhận trước khi đưa ra thị trường v&amp;agrave; sản phẩm phải được chứng minh bằng c&amp;aacute;c nghi&amp;ecirc;n cứu.&lt;/li&gt;\r\n	&lt;li&gt;X&amp;aacute;c nhận về cấu tr&amp;uacute;c chức năng (structure / function claims). Những thực phẩm n&amp;agrave;y d&amp;ugrave;ng để chuyển tải những lợi &amp;iacute;ch tiềm t&amp;agrave;ng (chứ chưa chắc chắn) đối với sức khỏe con người. V&amp;iacute; dụ thực phẩm bổ sung Oligofructose c&amp;oacute; t&amp;aacute;c dụng hỗ trợ đường ti&amp;ecirc;u h&amp;oacute;a, sản phẩm c&amp;oacute; chứa&amp;nbsp;&lt;a href=&quot;https://vi.wikipedia.org/w/index.php?title=Chondroitin&amp;amp;action=edit&amp;amp;redlink=1&quot;&gt;Chondroitin&lt;/a&gt;,&amp;nbsp;&lt;a href=&quot;https://vi.wikipedia.org/w/index.php?title=Glucosamin&amp;amp;action=edit&amp;amp;redlink=1&quot;&gt;Glucosamin&lt;/a&gt;,&amp;nbsp;&lt;a href=&quot;https://vi.wikipedia.org/w/index.php?title=Canxi_gluconate&amp;amp;action=edit&amp;amp;redlink=1&quot;&gt;Canxi gluconate&lt;/a&gt;&amp;nbsp;c&amp;oacute; t&amp;aacute;c dụng tăng dịch khớp, ph&amp;ograve;ng ngừa, hỗ trợ điều trị c&amp;aacute;c nguy cơ tho&amp;aacute;i h&amp;oacute;a hệ thống&amp;nbsp;&lt;a href=&quot;https://vi.wikipedia.org/w/index.php?title=X%C6%B0%C6%A1ng_kh%E1%BB%9Bp&amp;amp;action=edit&amp;amp;redlink=1&quot;&gt;xương khớp&lt;/a&gt;,&amp;nbsp;&lt;a href=&quot;https://vi.wikipedia.org/wiki/Lo%C3%A3ng_x%C6%B0%C6%A1ng&quot;&gt;lo&amp;atilde;ng xương&lt;/a&gt;&lt;a href=&quot;https://vi.wikipedia.org/wiki/Th%E1%BB%B1c_ph%E1%BA%A9m_ch%E1%BB%A9c_n%C4%83ng#cite_note-3&quot;&gt;[3]&lt;/a&gt;. Hoặc c&amp;aacute;c sản phẩm c&amp;oacute; chứa nhiều loại&amp;nbsp;&lt;a href=&quot;https://vi.wikipedia.org/wiki/Axit_amin&quot;&gt;acid amin&lt;/a&gt;&amp;nbsp;v&amp;agrave; c&amp;aacute;c&amp;nbsp;&lt;a href=&quot;https://vi.wikipedia.org/wiki/Nguy%C3%AAn_t%E1%BB%91_vi_l%C6%B0%E1%BB%A3ng&quot;&gt;nguy&amp;ecirc;n tố vi lượng&lt;/a&gt;:&amp;nbsp;&lt;a href=&quot;https://vi.wikipedia.org/wiki/K%E1%BA%BDm&quot;&gt;kẽm&lt;/a&gt;,&amp;nbsp;&lt;a href=&quot;https://vi.wikipedia.org/w/index.php?title=Iode&amp;amp;action=edit&amp;amp;redlink=1&quot;&gt;iode&lt;/a&gt;,&amp;nbsp;&lt;a href=&quot;https://vi.wikipedia.org/wiki/S%E1%BA%AFt&quot;&gt;sắt&lt;/a&gt;.... được gọi l&amp;agrave;&amp;nbsp;&lt;a href=&quot;https://vi.wikipedia.org/w/index.php?title=Nguy%C3%AAn_sinh_ch%E1%BA%A5t_men_bia_t%C6%B0%C6%A1i&amp;amp;action=edit&amp;amp;redlink=1&quot;&gt;nguy&amp;ecirc;n sinh chất men bia tươi&lt;/a&gt;&amp;nbsp;gi&amp;uacute;p trẻ tăng chiều cao&lt;a href=&quot;https://vi.wikipedia.org/wiki/Th%E1%BB%B1c_ph%E1%BA%A9m_ch%E1%BB%A9c_n%C4%83ng#cite_note-4&quot;&gt;[4]&lt;/a&gt;. Những x&amp;aacute;c nhận về cấu tr&amp;uacute;c/chức năng c&amp;oacute; thể do t&amp;aacute;c dụng đ&amp;atilde; được biết đến của một hay nhiều th&amp;agrave;nh phần c&amp;oacute; sẵn hoặc được bổ sung v&amp;agrave;o thực phẩm.&amp;nbsp;&lt;/li&gt;\r\n&lt;/ol&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li&gt;Để chọn lựa đ&amp;uacute;ng những thực phẩm cần thiết, người ti&amp;ecirc;u d&amp;ugrave;ng n&amp;ecirc;n đọc kỹ cả phần đối tượng sử dụng, liều d&amp;ugrave;ng, c&amp;ocirc;ng dụng, c&amp;aacute;c lưu &amp;yacute; đặc biệt xem c&amp;oacute; ph&amp;ugrave; hợp với mục đ&amp;iacute;ch sử dụng. Đừng qu&amp;ecirc;n xem t&amp;ecirc;n, địa chỉ của nh&amp;agrave; sản xuất để tr&amp;aacute;nh h&amp;agrave;ng giả, h&amp;agrave;ng nh&amp;aacute;i.&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n', '', '', '', 'thuc-pham-chuc-nang-la-gi', 4, 0, 0, 0, '2015-09-21 17:17:49', '2015-09-22 17:37:59', 1, 1, 1),
(18, 'Giá trị cốt lõi', '/public/images/Functional-foods-at-the-forefr-5600-7018-1387770506.jpg', '&lt;p&gt;&lt;span style=&quot;font-size:18px&quot;&gt;&lt;span style=&quot;font-family:times new roman,times,serif&quot;&gt;&lt;strong&gt;&lt;span style=&quot;background-color:#FFFFFF&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; Gi&amp;aacute; Trị Cốt L&amp;otilde;i:&lt;/span&gt;&lt;/strong&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\n', '&lt;table border=&quot;0&quot; cellpadding=&quot;0&quot; cellspacing=&quot;0&quot; style=&quot;width:500px&quot;&gt;\n	&lt;tbody&gt;\n		&lt;tr&gt;\n			&lt;td&gt;\n			&lt;ul style=&quot;list-style-type:circle&quot;&gt;\n				&lt;li&gt;Ph&amp;aacute;p lu&amp;acirc;̣t l&amp;agrave; thượng t&amp;ocirc;n của mọi h&amp;agrave;nh đ&amp;ocirc;̣ng.&lt;/li&gt;\n				&lt;li&gt;N&amp;acirc;ng cao nền&amp;nbsp; văn ho&amp;aacute;&amp;nbsp; Việt v&amp;agrave; vị thế quốc gia.&lt;/li&gt;\n				&lt;li&gt;C&amp;ocirc;ng bằng, minh bạch, li&amp;ecirc;m ch&amp;iacute;nh trong mọi hoạt đ&amp;ocirc;̣ng v&amp;agrave; c&amp;aacute;c m&amp;ocirc;́i quan h&amp;ecirc;̣ với kh&amp;aacute;ch h&amp;agrave;ng, nh&amp;agrave; ph&amp;acirc;n ph&amp;ocirc;́i v&amp;agrave; c&amp;aacute;c nh&amp;acirc;n vi&amp;ecirc;n trong c&amp;ocirc;ng ty.&lt;/li&gt;\n				&lt;li&gt;Đ&amp;ecirc;̀ cao gi&amp;aacute; trị c&amp;aacute; nh&amp;acirc;n, m&amp;ocirc;̃i c&amp;aacute; nh&amp;acirc;n xứng đ&amp;aacute;ng nh&amp;acirc;̣n được sự t&amp;ocirc;n trọng, được đ&amp;ocirc;́i xứ c&amp;ocirc;ng bằng v&amp;agrave; c&amp;oacute; cơ h&amp;ocirc;̣i khẳng định, th&amp;agrave;nh c&amp;ocirc;ng khi c&amp;ugrave;ng hợp t&amp;aacute;c với Sen Việt Group.&lt;/li&gt;\n				&lt;li&gt;Ch&amp;uacute;ng t&amp;ocirc;i coi trọng th&amp;agrave;nh tựu đạt được th&amp;ocirc;ng qua vi&amp;ecirc;̣c n&amp;ocirc;̃ lực v&amp;agrave; ho&amp;agrave;n th&amp;agrave;nh khả năng c&amp;aacute; nh&amp;acirc;n, t&amp;ocirc;̉ chức.&amp;nbsp; Ch&amp;uacute;ng t&amp;ocirc;i đ&amp;ecirc;̀ cao sự s&amp;aacute;ng tạo, lu&amp;ocirc;n đ&amp;acirc;̀u tư nhi&amp;ecirc;̀u hơn cho gi&amp;aacute; trị con người.&lt;/li&gt;\n			&lt;/ul&gt;\n			&lt;/td&gt;\n		&lt;/tr&gt;\n	&lt;/tbody&gt;\n&lt;/table&gt;\n\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\n', '', '', '', 'gioi-thieu/gia-tri-cot-loi', 2, 0, 1, 3, '2015-10-08 10:47:23', '2015-10-17 04:32:37', 1, 0, 0),
(20, 'Bao bì Sen Việt Group dùng mực thân thiện môi trường', '/public/images/What-is-Eco-friendly-ink.png', '&lt;p&gt;&amp;Yacute; thức bảo vệ m&amp;ocirc;i trường lu&amp;ocirc;n được Sen Việt Group nh&amp;igrave;n nhận l&amp;agrave; m&amp;ocirc;t trong những ch&amp;igrave;a kh&amp;oacute;a&amp;nbsp;&lt;/p&gt;\n\n&lt;p&gt;gi&amp;uacute;p DN tồn tại, ph&amp;aacute;t triển vươn l&amp;ecirc;n tầm cao mới. Ch&amp;uacute;ng t&amp;ocirc;i rất tự h&amp;agrave;o v&amp;agrave;o những mục ti&amp;ecirc;u m&amp;agrave;&amp;nbsp;&lt;/p&gt;\n\n&lt;p&gt;ch&amp;uacute;ng t&amp;ocirc;i hướng tới trong sản xuất kinh doanh l&amp;agrave; tạo ra những sản phẩm đem lại gi&amp;aacute; trị đ&amp;iacute;ch thức&amp;nbsp;&lt;/p&gt;\n\n&lt;p&gt;về kinh tế cũng như m&amp;ocirc;i trường.&lt;/p&gt;\n\n&lt;p&gt;Những mục ti&amp;ecirc;u h&amp;agrave;ng đầu trong sự ph&amp;aacute;t triển của DN l&amp;agrave; kinh doanh c&amp;aacute;c d&amp;ograve;ng sản phẩm chăm s&amp;oacute;c&lt;/p&gt;\n\n&lt;p&gt;sức khỏe m&amp;agrave; ch&amp;uacute;ng t&amp;ocirc;i đ&amp;atilde; v&amp;agrave; đang hướng tới nhằm g&amp;oacute;p phần bảo vệ sức khỏe con người v&amp;agrave; giữ&amp;nbsp;&lt;/p&gt;\n\n&lt;p&gt;g&amp;igrave;n tr&amp;aacute;i đất m&amp;atilde;i xanh tươi.Đ&amp;oacute; l&amp;agrave; bao b&amp;igrave; sản phẩm &amp;aacute;p dụng quy tr&amp;igrave;nh 3R v&amp;agrave; sử dụng mực in than&lt;/p&gt;\n\n&lt;p&gt;thiện với m&amp;ocirc;i trường.&lt;/p&gt;\n', '&lt;p&gt;Như ch&amp;uacute;ng ta đ&amp;atilde; biết:&lt;/p&gt;\n\n&lt;p&gt;&amp;ldquo;Ng&amp;agrave;nh In v&amp;agrave; Bao B&amp;igrave; l&amp;agrave; một trong những ng&amp;agrave;nh c&amp;ocirc;ng nghiệp c&amp;oacute; tốc độ tăng trưởng mạnh tr&amp;ecirc;n thế&amp;nbsp;&lt;/p&gt;\n\n&lt;p&gt;giới. Ở Việt Nam, tốc độ tăng trưởng trung b&amp;igrave;nh của ng&amp;agrave;nh l&amp;ecirc;n đến hơn 20% trong suốt một thập&amp;nbsp;&lt;/p&gt;\n\n&lt;p&gt;kỷ qua. Đối với những loại mực in đang phổ biến tr&amp;ecirc;n thị trường hiện nay, th&amp;agrave;nh phần ch&amp;iacute;nh để&amp;nbsp;&lt;/p&gt;\n\n&lt;p&gt;tổng hợp sản xuất bao gồm c&amp;aacute;c chất v&amp;ocirc; cơ như: thuốc nhuộm, nhựa, dầu lanh, dầu kho&amp;aacute;ng v&amp;agrave;&lt;/p&gt;\n\n&lt;p&gt;nhiều chất phụ gia ... Tuy nhi&amp;ecirc;n, c&amp;aacute;c chất n&amp;agrave;y đều kh&amp;ocirc;ng th&amp;acirc;n thiện với m&amp;ocirc;i trường. V&amp;igrave; thế,&amp;nbsp;&lt;/p&gt;\n\n&lt;p&gt;mực in được xếp v&amp;agrave;o danh mục c&amp;aacute;c chất thải nguy hại.&lt;/p&gt;\n\n&lt;p&gt;Mỗi năm, thế giới ti&amp;ecirc;u tốn khoảng hơn 550 triệu l&amp;iacute;t dầu để sản xuất mực phục vụ ng&amp;agrave;nh in ấn.&amp;nbsp;&lt;/p&gt;\n\n&lt;p&gt;Với tốc độ ti&amp;ecirc;u thụ như tr&amp;ecirc;n, ng&amp;agrave;nh c&amp;ocirc;ng nghiệp n&amp;agrave;y c&amp;ugrave;ng với nhiều hoạt động kh&amp;aacute;c của con&amp;nbsp;&lt;/p&gt;\n\n&lt;p&gt;người, đ&amp;atilde; g&amp;oacute;p phần l&amp;agrave;m gia tăng lượng kh&amp;iacute; CO2 g&amp;acirc;y hiệu ứng nh&amp;agrave; k&amp;iacute;nh của tr&amp;aacute;i đất. Hơn thế, việc&amp;nbsp;&lt;/p&gt;\n\n&lt;p&gt;sử dụng c&amp;aacute;c loại mực in kh&amp;ocirc;ng c&amp;oacute; nguồn gốc hoặc c&amp;aacute;c loại c&amp;oacute; chất lượng thấp c&amp;ograve;n dẫn đến nồng&amp;nbsp;&lt;/p&gt;\n\n&lt;p&gt;độ ph&amp;aacute;t thải Crom cao, g&amp;acirc;y ảnh hưởng đến sức khỏe người ti&amp;ecirc;u d&amp;ugrave;ng v&amp;agrave; nghi&amp;ecirc;m trọng hơn l&amp;agrave; c&amp;oacute;&amp;nbsp;&lt;/p&gt;\n\n&lt;p&gt;thể g&amp;acirc;y ra bệnh ung thư. Do đ&amp;oacute;, việc ưu ti&amp;ecirc;n d&amp;ugrave;ng c&amp;aacute;c c&amp;ocirc;ng nghệ v&amp;agrave; sản phẩm th&amp;acirc;n thiện với m&amp;ocirc;i&amp;nbsp;&lt;/p&gt;\n\n&lt;p&gt;trường đang ng&amp;agrave;y c&amp;agrave;ng ph&amp;aacute;t triển ở nhiều lĩnh vực, trong đ&amp;oacute; c&amp;oacute; ng&amp;agrave;nh c&amp;ocirc;ng nghiệp in ấn.&lt;/p&gt;\n\n&lt;p&gt;Hiện nay, c&amp;oacute; 3 loại &amp;ldquo;Mực Xanh&amp;rdquo;, loại mực th&amp;acirc;n thiện với m&amp;ocirc;i trường, bao gồm c&amp;aacute;c loại mực in&amp;nbsp;&lt;/p&gt;\n\n&lt;p&gt;gốc nước (water-based), mực in UV v&amp;agrave; mực in cồn (alcohol). Mực in gốc nước hiện được sử&amp;nbsp;&lt;/p&gt;\n\n&lt;p&gt;dụng rộng r&amp;atilde;i trong in Flexo cho bao b&amp;igrave; thực phẩm, dược phẩm v&amp;agrave; thuốc l&amp;aacute;. Nếu c&amp;aacute;c kỹ thuật ứng&amp;nbsp;&lt;/p&gt;\n\n&lt;p&gt;dụng được cải tiến th&amp;ecirc;m, n&amp;oacute; được ti&amp;ecirc;n đo&amp;aacute;n sẽ dần thay thế mực in gốc dung m&amp;ocirc;i (Solvent-&lt;br /&gt;\nbased). Mực in gốc đậu n&amp;agrave;nh hay mực in gốc thực vật, ng&amp;agrave;y nay đ&amp;atilde; được sử dụng phổ biến tại&amp;nbsp;&lt;/p&gt;\n\n&lt;p&gt;Hoa Kỳ v&amp;agrave; Nhật Bản. Mực in gốc đậu n&amp;agrave;nh v&amp;agrave; thực vật dễ d&amp;agrave;ng t&amp;aacute;ch ra khỏi vật in, đ&amp;acirc;y l&amp;agrave; điều&amp;nbsp;&lt;/p&gt;\n\n&lt;p&gt;đặc biệt quan trọng trong việc t&amp;aacute;i chế giấy. Mực in gốc dầu mỏ (Petroleum-based) c&amp;oacute; h&amp;agrave;m lượng&amp;nbsp;&lt;/p&gt;\n\n&lt;p&gt;30-35% hợp chất hữu cơ dễ bay hơi (VOCs), trong khi đ&amp;oacute; mực in gốc đậu n&amp;agrave;nh, chất VOCs chỉ ở&amp;nbsp;&lt;/p&gt;\n\n&lt;p&gt;mức 0-5%.&lt;/p&gt;\n\n&lt;p&gt;Tại những nước ph&amp;aacute;t triển, mực in gốc hợp chất thơm (aromatic based) bị cấm triệt để d&amp;ugrave;ng&amp;nbsp;&lt;/p&gt;\n\n&lt;p&gt;trong bao b&amp;igrave; thực phẩm. Tại Anh Quốc, mực in gốc dung m&amp;ocirc;i xem l&amp;agrave; bất hợp ph&amp;aacute;p khi sử dụng in&amp;nbsp;&lt;/p&gt;\n\n&lt;p&gt;m&amp;agrave;ng d&amp;ugrave;ng trong bao b&amp;igrave; thực phẩm kể từ th&amp;aacute;ng 06/2000. Tại Hoa Kỳ, cơ quan bảo vệ m&amp;ocirc;i trường&amp;nbsp;&lt;/p&gt;\n\n&lt;p&gt;(EPA) phản đối quyết liệt việc sử dụng mực in gốc dung m&amp;ocirc;i, v&amp;agrave; chỉ c&amp;oacute; mực in flexo gốc nước&amp;nbsp;&lt;/p&gt;\n\n&lt;p&gt;mới được chấp nhận đủ điều kiện d&amp;ugrave;ng cho bao b&amp;igrave; dược phẩm v&amp;agrave; thực phẩm.&lt;/p&gt;\n\n&lt;p&gt;Năm 1994, đạo luật mực in thực vật (Hoa Kỳ) bắt buộc hợp đồng của c&amp;aacute;c cơ quan ch&amp;iacute;nh phủ v&amp;agrave;&amp;nbsp;&lt;/p&gt;\n\n&lt;p&gt;c&amp;aacute;c nh&amp;agrave; in phải ưu ti&amp;ecirc;n d&amp;ugrave;ng mực in gốc dầu thực vật hơn mực in gốc dầu mỏ bay hơi, ở bất kỳ&amp;nbsp;&lt;/p&gt;\n\n&lt;p&gt;l&amp;uacute;c n&amp;agrave;o c&amp;oacute; thể được. Đ&amp;acirc;y l&amp;agrave; nổ lực nhằm giảm lượng kh&amp;iacute; tho&amp;aacute;t ra từ hợp chất hữu cơ bay hơi v&amp;agrave;&amp;nbsp;&lt;/p&gt;\n\n&lt;p&gt;g&amp;acirc;y nguy hiểm &amp;ocirc; nhiễm cho bầu kh&amp;iacute; quyển.&lt;/p&gt;\n\n&lt;p&gt;Trung ki&amp;ecirc;n Page 1&lt;/p&gt;\n\n&lt;p&gt;Bao b&amp;igrave; Sen Việt Group d&amp;ugrave;ng mực th&amp;acirc;n thiện m&amp;ocirc;i trường&lt;/p&gt;\n\n&lt;p&gt;Ng&amp;agrave;y nay, với c&amp;aacute;c quy định chặt chẽ của ch&amp;iacute;nh phủ ở hầu hết c&amp;aacute;c nước ph&amp;aacute;t triển li&amp;ecirc;n quan việc&amp;nbsp;&lt;/p&gt;\n\n&lt;p&gt;sử dụng c&amp;aacute;c chất độc hại, cũng như lời k&amp;ecirc;u gọi sản xuất &amp;ldquo;bao b&amp;igrave; ph&amp;ugrave; hợp m&amp;ocirc;i trường&amp;rdquo; từ c&amp;aacute;c tổ&amp;nbsp;&lt;/p&gt;\n\n&lt;p&gt;chức m&amp;ocirc;i trường đ&amp;atilde; th&amp;uacute;c đẩy đẩy ng&amp;agrave;nh c&amp;ocirc;ng nghiệp mực in hướng tới việc định hướng th&amp;acirc;n&amp;nbsp;&lt;/p&gt;\n\n&lt;p&gt;thiện m&amp;ocirc;i trường, hướng tới việc ph&amp;aacute;t triển bền vững v&amp;agrave; cuối c&amp;ugrave;ng loại bỏ những nh&amp;agrave; m&amp;aacute;y sản&amp;nbsp;&lt;/p&gt;\n\n&lt;p&gt;xuất mực in kh&amp;ocirc;ng th&amp;acirc;n thiện với m&amp;ocirc;i trường. Xu hướng ph&amp;aacute;t triển n&amp;agrave;y kh&amp;ocirc;ng chỉ mang lại lợi&amp;nbsp;&lt;/p&gt;\n\n&lt;p&gt;&amp;iacute;ch cho ch&amp;iacute;nh ng&amp;agrave;nh mực in v&amp;agrave; bao b&amp;igrave; m&amp;agrave; c&amp;ograve;n ảnh hưởng s&amp;acirc;u sắc đến c&amp;aacute;c ng&amp;agrave;nh kinh doanh li&amp;ecirc;n&amp;nbsp;&lt;/p&gt;\n\n&lt;p&gt;quan như thực phẩm, dược phẩm..vv.&amp;rdquo;&lt;/p&gt;\n\n&lt;p&gt;Qua đ&amp;oacute;, Ch&amp;uacute;ng ta c&amp;oacute; thể thấy được rằng việc chăm s&amp;oacute;c sức khỏe cho bản bản th&amp;acirc;n l&amp;agrave; tốt nhưng&amp;nbsp;&lt;/p&gt;\n\n&lt;p&gt;nếu chỉ l&amp;agrave;m như vậy m&amp;agrave; rồi thải ra m&amp;ocirc;i trường quanh ch&amp;uacute;ng ta những r&amp;aacute;c thải độc hại g&amp;acirc;y ảnh&lt;/p&gt;\n\n&lt;p&gt;hưởng đến cộng đồng, đến nguồn nước, đến m&amp;ocirc;i trường sống của ch&amp;iacute;nh ch&amp;uacute;ng ta th&amp;igrave; c&amp;oacute; n&amp;ecirc;n&amp;nbsp;&lt;/p&gt;\n\n&lt;p&gt;kh&amp;ocirc;ng? Nếu ch&amp;uacute;ng ta kh&amp;ocirc;ng g&amp;oacute;p phần bảo vệ m&amp;ocirc;i trường th&amp;igrave; c&amp;aacute; nh&amp;acirc;n mỗi ch&amp;uacute;ng ta khỏe mạnh&lt;/p&gt;\n\n&lt;p&gt;liệu rằng đ&amp;atilde; thật sự c&amp;oacute; &amp;iacute;ch chưa?&lt;/p&gt;\n\n&lt;p&gt;H&amp;atilde;y c&amp;ugrave;ng Sen Việt Group d&amp;ugrave;ng sản phẩm c&amp;oacute; bao b&amp;igrave; th&amp;acirc;n thi&amp;ecirc;n m&amp;ocirc;i trường.&lt;/p&gt;\n', '', '', '', 'bao-bi-sen-viet-group-dung-muc-than-thien-moi-truong', 13, 0, 1, 0, '2015-10-12 03:31:16', '2015-10-12 03:31:16', 1, 1, 1),
(21, 'Tiêu chuẩn về bao bì sản phẩm của Sen Việt Group', '/public/images/3r3.jpg', '&lt;p&gt;&lt;strong&gt;3R - giải ph&amp;aacute;p hữu hiệu để bảo vệ m&amp;ocirc;i trường&lt;/strong&gt;&lt;br /&gt;\n-&amp;nbsp;&lt;strong&gt;Tiết giảm &lt;/strong&gt;(Reduce)&lt;/p&gt;\n\n&lt;p&gt;&lt;strong&gt;- T&amp;aacute;i sử dụng &lt;/strong&gt;(Reuse)&lt;/p&gt;\n\n&lt;p&gt;&lt;strong&gt;- T&amp;aacute;i chế &lt;/strong&gt;(Recycle)&lt;/p&gt;\n', '&lt;p&gt;&amp;nbsp;&lt;/p&gt;\n\n&lt;p&gt;&lt;strong&gt;I/ C&amp;aacute;c ch&lt;/strong&gt;&lt;strong&gt;ỉ &lt;/strong&gt;&lt;strong&gt;ti&amp;ecirc;u v&lt;/strong&gt;&lt;strong&gt;ề &lt;/strong&gt;&lt;strong&gt;s&lt;/strong&gt;&lt;strong&gt;ản &lt;/strong&gt;&lt;strong&gt;ph&lt;/strong&gt;&lt;strong&gt;ẩm &lt;/strong&gt;&lt;strong&gt;c&lt;/strong&gt;&lt;strong&gt;ủa &lt;/strong&gt;&lt;strong&gt;Sen Vi&lt;/strong&gt;&lt;strong&gt;ệt &lt;/strong&gt;&lt;strong&gt;Group:&lt;/strong&gt;&lt;br /&gt;\n&lt;strong&gt;1 &lt;/strong&gt;&lt;strong&gt;&amp;ndash; &lt;/strong&gt;&lt;strong&gt;H&amp;agrave;ng Vi&lt;/strong&gt;&lt;strong&gt;ệ&lt;/strong&gt;&lt;strong&gt;t Nam ch&lt;/strong&gt;&lt;strong&gt;ấ&lt;/strong&gt;&lt;strong&gt;t l&lt;/strong&gt;&lt;strong&gt;ượ&lt;/strong&gt;&lt;strong&gt;ng cao:&lt;/strong&gt;&lt;br /&gt;\n+ C&amp;aacute;c sản phẩm được người ti&amp;ecirc;u d&amp;ugrave;ng b&amp;igrave;nh chọn v&amp;agrave; đ&amp;aacute;nh gi&amp;aacute; về chất lượng.&lt;br /&gt;\n+ Sản phẩm c&amp;oacute; nguồn gốc xuất xứ r&amp;otilde; r&amp;agrave;ng, tr&amp;ecirc;n bao b&amp;igrave; thể hiện đầy đủ c&amp;aacute;c th&amp;ocirc;ng tin&lt;br /&gt;\nvề m&amp;atilde; quốc gia sản xuất, m&amp;atilde; ng&amp;agrave;nh, m&amp;atilde; c&amp;ocirc;ng ty, m&amp;atilde; sản phẩm.Điều n&amp;agrave;y c&amp;oacute; thể sử&lt;br /&gt;\ndụng c&amp;aacute;c ứng dụng qu&amp;eacute;t m&amp;atilde; số m&amp;atilde; vạch tr&amp;ecirc;n vỏ sản phẩm.&lt;br /&gt;\n&lt;strong&gt;2 &lt;/strong&gt;&lt;strong&gt;&amp;ndash; &lt;/strong&gt;&lt;strong&gt;V&amp;igrave; quy&lt;/strong&gt;&lt;strong&gt;ề&lt;/strong&gt;&lt;strong&gt;n l&lt;/strong&gt;&lt;strong&gt;ợ&lt;/strong&gt;&lt;strong&gt;i c&lt;/strong&gt;&lt;strong&gt;ủ&lt;/strong&gt;&lt;strong&gt;a ng&lt;/strong&gt;&lt;strong&gt;ườ&lt;/strong&gt;&lt;strong&gt;i ti&amp;ecirc;u dung:&lt;/strong&gt;&lt;br /&gt;\n+ V&amp;igrave; sức khỏe cộng đồng&lt;br /&gt;\n+ Sản phẩm ph&amp;ugrave; hợp thu nhập b&amp;igrave;nh qu&amp;acirc;n&lt;br /&gt;\n+ th&amp;uacute;c đẩy việc canh t&amp;aacute;c, nu&amp;ocirc;i trồng dược liệu qu&amp;yacute; hiếm&lt;br /&gt;\n&lt;strong&gt;4 &lt;/strong&gt;&lt;strong&gt;&amp;ndash;&lt;/strong&gt;&lt;strong&gt;Ngu&lt;/strong&gt;&lt;strong&gt;ồ&lt;/strong&gt;&lt;strong&gt;n g&lt;/strong&gt;&lt;strong&gt;ố&lt;/strong&gt;&lt;strong&gt;c nguy&amp;ecirc;n li&lt;/strong&gt;&lt;strong&gt;ệ&lt;/strong&gt;&lt;strong&gt;u t&lt;/strong&gt;&lt;strong&gt;ừ &lt;/strong&gt;&lt;strong&gt;thi&amp;ecirc;n nhi&amp;ecirc;n.&lt;/strong&gt;&lt;br /&gt;\n&lt;strong&gt;5 &lt;/strong&gt;&lt;strong&gt;&amp;ndash;Đạ&lt;/strong&gt;&lt;strong&gt;t &lt;/strong&gt;&lt;strong&gt;đượ&lt;/strong&gt;&lt;strong&gt;c c&amp;aacute;c ti&amp;ecirc;u ch&amp;iacute; v&lt;/strong&gt;&lt;strong&gt;ề &lt;/strong&gt;&lt;strong&gt;TPCN c&amp;aacute;c n&lt;/strong&gt;&lt;strong&gt;ướ&lt;/strong&gt;&lt;strong&gt;c ASIAN v&amp;agrave; Ch&amp;acirc;u &amp;Aacute;&lt;/strong&gt;&lt;br /&gt;\n+V&amp;igrave; m&amp;ocirc;i trường sống&lt;br /&gt;\n+ Kh&amp;ocirc;ng t&amp;aacute;c dụng phụ&lt;br /&gt;\n+ Chu tr&amp;igrave;nh 3R -Reuse, Reduce, Recycle(tiết giảm,t&amp;aacute;i sản xuất, t&amp;aacute;i sử dụng )&lt;br /&gt;\n&lt;strong&gt;6 &lt;/strong&gt;&lt;strong&gt;&amp;ndash; &lt;/strong&gt;&lt;strong&gt;Ph&amp;aacute;t tri&lt;/strong&gt;&lt;strong&gt;ể&lt;/strong&gt;&lt;strong&gt;n &lt;/strong&gt;&lt;strong&gt;đượ&lt;/strong&gt;&lt;strong&gt;c c&amp;aacute;c b&amp;agrave;i thu&lt;/strong&gt;&lt;strong&gt;ố&lt;/strong&gt;&lt;strong&gt;c truy&lt;/strong&gt;&lt;strong&gt;ề&lt;/strong&gt;&lt;strong&gt;n th&lt;/strong&gt;&lt;strong&gt;ố&lt;/strong&gt;&lt;strong&gt;ng v&amp;agrave; ngu&lt;/strong&gt;&lt;strong&gt;ồ&lt;/strong&gt;&lt;strong&gt;n d&lt;/strong&gt;&lt;strong&gt;ượ&lt;/strong&gt;&lt;strong&gt;c li&lt;/strong&gt;&lt;strong&gt;ệ&lt;/strong&gt;&lt;strong&gt;u c&lt;/strong&gt;&lt;strong&gt;ủ&lt;/strong&gt;&lt;strong&gt;a Vi&lt;/strong&gt;&lt;strong&gt;ệ&lt;/strong&gt;&lt;strong&gt;t&lt;/strong&gt;&lt;br /&gt;\n&lt;strong&gt;Nam.&lt;/strong&gt;&lt;br /&gt;\n&lt;strong&gt;II/ Th&amp;ocirc;ng tin th&amp;ecirc;m v&lt;/strong&gt;&lt;strong&gt;ề &lt;/strong&gt;&lt;strong&gt;Chu tr&amp;igrave;nh 3R&lt;/strong&gt;&lt;br /&gt;\n&lt;strong&gt;3R - &lt;/strong&gt;&lt;strong&gt;giải ph&amp;aacute;p hữu hiệu để bảo vệ m&amp;ocirc;i trường&lt;/strong&gt;&lt;br /&gt;\n3R l&amp;agrave; từ viết tắt ba chữ c&amp;aacute;i đầu trong tiếng Anh: Reduce - Reuse - Recycle. Theo&lt;br /&gt;\nnghĩa tiếng Việt l&amp;agrave;: Tiết giảm - T&amp;aacute;i sử dụng - T&amp;aacute;i chế, gọi tắt l&amp;agrave; 3T. Đ&amp;acirc;y l&amp;agrave; giải&lt;br /&gt;\nph&amp;aacute;p quen thuộc đối với nhiều nước tr&amp;ecirc;n thế giới gi&amp;uacute;p giảm nhẹ g&amp;aacute;nh nặng l&amp;ecirc;n m&amp;ocirc;i&lt;br /&gt;\ntrường sống.&lt;br /&gt;\n&lt;strong&gt;Tiết giảm &lt;/strong&gt;(Reduce) l&amp;agrave; việc giảm lượng r&amp;aacute;c ph&amp;aacute;t sinh th&amp;ocirc;ng qua việc thay đổi lối&lt;br /&gt;\nsống, thay đổi trong c&amp;aacute;ch ti&amp;ecirc;u d&amp;ugrave;ng, cải tiến c&amp;aacute;c quy tr&amp;igrave;nh sản xuất&amp;hellip; Chẳng hạn &amp;aacute;p&lt;br /&gt;\ndụng c&amp;aacute;c giải ph&amp;aacute;p sản xuất sạch hơn, h&amp;oacute;a học xanh trong hoạt động sản xuất, hay&amp;nbsp;&lt;/p&gt;\n\n&lt;p&gt;khuyến kh&amp;iacute;ch th&amp;oacute;i quen &amp;ldquo;ăn chắc mặc bền&amp;rdquo; trong đời sống, sinh hoạt của người&lt;br /&gt;\nd&amp;acirc;n. Đ&amp;acirc;y l&amp;agrave; nội dung hiệu quả nhất trong ba giải ph&amp;aacute;p, l&amp;agrave; sự tối ưu h&amp;oacute;a qu&amp;aacute; tr&amp;igrave;nh&lt;br /&gt;\nsản xuất v&amp;agrave; ti&amp;ecirc;u d&amp;ugrave;ng về mặt m&amp;ocirc;i trường, tạo ra lượng sản phẩm lớn nhất, sử dụng&lt;br /&gt;\nhiệu quả nhất m&amp;agrave; ti&amp;ecirc;u thụ &amp;iacute;t t&amp;agrave;i nguy&amp;ecirc;n v&amp;agrave; thải ra lượng thải thấp nhất.&lt;br /&gt;\n&lt;strong&gt;T&amp;aacute;i sử dụng &lt;/strong&gt;(Reuse) l&amp;agrave; việc sử dụng lại c&amp;aacute;c sản phẩm, hay một phần của sản phẩm&lt;br /&gt;\ncho ch&amp;iacute;nh mục đ&amp;iacute;ch cũ, hay cho một mục đ&amp;iacute;ch kh&amp;aacute;c, sử dụng một sản phẩm nhiều&lt;br /&gt;\nlần cho đến hết tuổi thọ sản phẩm.&lt;br /&gt;\n&lt;strong&gt;T&amp;aacute;i chế &lt;/strong&gt;(Recycle) l&amp;agrave; việc sử dụng r&amp;aacute;c thải, vật liệu thải l&amp;agrave;m nguy&amp;ecirc;n liệu sản xuất ra&lt;br /&gt;\nc&amp;aacute;c vật chất, c&amp;aacute;c sản phẩm mới c&amp;oacute; &amp;iacute;ch. Hoạt động thu hồi lại từ chất thải c&amp;aacute;c th&amp;agrave;nh&lt;br /&gt;\nphần c&amp;oacute; thể sử dụng để chế biến th&amp;agrave;nh c&amp;aacute;c sản phẩm mới sử dụng lại cho c&amp;aacute;c hoạt&lt;br /&gt;\nđộng sinh hoạt v&amp;agrave; sản xuất. Mặc d&amp;ugrave; chất lượng của sản phẩm t&amp;aacute;i chế kh&amp;ocirc;ng thể&lt;br /&gt;\nbằng sản phẩm từ nguy&amp;ecirc;n liệu ch&amp;iacute;nh phẩm nhưng qu&amp;aacute; tr&amp;igrave;nh n&amp;agrave;y gi&amp;uacute;p ngăn chặn&lt;br /&gt;\nl&amp;atilde;ng ph&amp;iacute; nguồn t&amp;agrave;i nguy&amp;ecirc;n, giảm ti&amp;ecirc;u thụ nguy&amp;ecirc;n liệu th&amp;ocirc; cũng như nhi&amp;ecirc;n liệu sử&lt;br /&gt;\ndụng so với qu&amp;aacute; tr&amp;igrave;nh sản xuất cơ bản từ nguy&amp;ecirc;n liệu th&amp;ocirc;. T&amp;aacute;i chế c&amp;oacute; thể chia th&amp;agrave;nh&lt;br /&gt;\nhai dạng, t&amp;aacute;i chế ngay tại nguồn từ quy tr&amp;igrave;nh sản xuất v&amp;agrave; t&amp;aacute;i chế nguy&amp;ecirc;n liệu từ sản&lt;br /&gt;\nphẩm thải.&lt;br /&gt;\n&lt;strong&gt;III/ S&lt;/strong&gt;&lt;strong&gt;ự ư&lt;/strong&gt;&lt;strong&gt;u vi&lt;/strong&gt;&lt;strong&gt;ệ&lt;/strong&gt;&lt;strong&gt;t bao b&amp;igrave; c&lt;/strong&gt;&lt;strong&gt;ủ&lt;/strong&gt;&lt;strong&gt;a s&lt;/strong&gt;&lt;strong&gt;ả&lt;/strong&gt;&lt;strong&gt;n ph&lt;/strong&gt;&lt;strong&gt;ẩ&lt;/strong&gt;&lt;strong&gt;m:&lt;/strong&gt;&lt;br /&gt;\nC&amp;aacute;c sản phẩm của Sen Việt Group được sử dụng bao b&amp;igrave; từ nhựa PET v&amp;agrave; giấy,&lt;br /&gt;\nkh&amp;ocirc;ng sử dụng nilong . Mực in tr&amp;ecirc;n bao b&amp;igrave; l&amp;agrave; mực hữu cơ, kh&amp;ocirc;ng g&amp;acirc;y hại sức khỏe&lt;br /&gt;\ncho người sử dụng v&amp;agrave; th&amp;acirc;n thiện với m&amp;ocirc;i trường. Điều n&amp;agrave;y cũng l&amp;agrave; một trong&lt;br /&gt;\nnhững điểm ưu việt v&amp;agrave; t&amp;iacute;nh tr&amp;aacute;ch nhiệm của doanh nghiệp với cộng đồng v&amp;agrave; người&lt;br /&gt;\nti&amp;ecirc;u d&amp;ugrave;ng.&lt;br /&gt;\n&amp;nbsp;&lt;/p&gt;\n', '', '', '', 'tieu-chuan-ve-bao-bi-san-pham-cua-sen-viet-group', 13, 0, 1, 0, '2015-10-12 03:38:26', '2015-10-15 03:37:06', 1, 1, 1);
INSERT INTO `cms_news` (`id`, `title`, `thumbnail`, `description`, `content`, `meta_title`, `meta_keyword`, `meta_description`, `alias`, `category_id`, `is_featured`, `publish`, `order`, `created_date`, `updated_date`, `userid_created`, `fb_share_like`, `fb_comment`) VALUES
(22, 'Nghị định Về quản lý hoạt động bán hàng đa cấp', '/public/images/Bai1_giaychungnhan.png', '&lt;p&gt;Nghị định 42 của ch&amp;iacute;nh phủ về hoạt động b&amp;aacute;n h&amp;agrave;ng đa cấp&lt;/p&gt;\n', '&lt;table border=&quot;0&quot; style=&quot;width:100%&quot;&gt;\n	&lt;tbody&gt;\n		&lt;tr&gt;\n			&lt;td&gt;\n			&lt;p&gt;&lt;strong&gt;CH&amp;Iacute;NH PHỦ&lt;/strong&gt;&lt;/p&gt;\n\n			&lt;p&gt;Số: 110/2005/NĐ-CP&lt;/p&gt;\n			&lt;/td&gt;\n			&lt;td&gt;&amp;nbsp;&lt;/td&gt;\n			&lt;td&gt;\n			&lt;p&gt;&lt;strong&gt;CỘNG HO&amp;Agrave; X&amp;Atilde; HỘI CHỦ NGHĨA VIỆT NAM&lt;/strong&gt;&lt;/p&gt;\n\n			&lt;p&gt;&lt;strong&gt;Độc lập - Tự do - Hạnh ph&amp;uacute;c&lt;/strong&gt;&lt;/p&gt;\n\n			&lt;p&gt;&lt;em&gt;H&amp;agrave; Nội, ng&amp;agrave;y 24&amp;nbsp;th&amp;aacute;ng 08&amp;nbsp;năm 2005&lt;/em&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/p&gt;\n			&lt;/td&gt;\n		&lt;/tr&gt;\n		&lt;tr&gt;\n			&lt;td colspan=&quot;3&quot;&gt;&amp;nbsp;&lt;/td&gt;\n		&lt;/tr&gt;\n		&lt;tr&gt;\n			&lt;td colspan=&quot;3&quot;&gt;\n			&lt;p&gt;&lt;strong&gt;NGHỊ ĐỊNH CỦA CH&amp;Iacute;NH PHỦ&lt;/strong&gt;&lt;/p&gt;\n\n			&lt;p&gt;&lt;strong&gt;&lt;em&gt;Về quản l&amp;yacute; hoạt động b&amp;aacute;n h&amp;agrave;ng đa cấp&lt;/em&gt;&lt;/strong&gt;&lt;/p&gt;\n\n			&lt;p&gt;&lt;strong&gt;CH&amp;Iacute;NH PHỦ&lt;/strong&gt;&lt;/p&gt;\n\n			&lt;p&gt;&lt;em&gt;Căn cứ Luật Tổ chức Ch&amp;iacute;nh phủ ng&amp;agrave;y 25 th&amp;aacute;ng 12 năm 2001;&lt;/em&gt;&lt;/p&gt;\n\n			&lt;p&gt;&lt;em&gt;Căn cứ Luật Cạnh tranh ng&amp;agrave;y 03 th&amp;aacute;ng 12 năm 2004;&lt;/em&gt;&lt;/p&gt;\n\n			&lt;p&gt;&lt;em&gt;Theo đề nghị của Bộ trưởng Bộ Thương mại,&lt;/em&gt;&lt;/p&gt;\n\n			&lt;p&gt;&lt;strong&gt;NGHỊ ĐỊNH:&lt;/strong&gt;&lt;/p&gt;\n\n			&lt;p&gt;&lt;strong&gt;Chương&amp;nbsp;&lt;a name=&quot;Chuong_I&quot;&gt;&lt;/a&gt;I&lt;/strong&gt;&lt;/p&gt;\n\n			&lt;p&gt;&lt;strong&gt;NHỮNG QUY ĐỊNH CHUNG&lt;/strong&gt;&lt;/p&gt;\n\n			&lt;p&gt;&lt;strong&gt;Điều&amp;nbsp;&lt;a name=&quot;Dieu_1&quot;&gt;&lt;/a&gt;1. Phạm vi điều chỉnh&lt;/strong&gt;&lt;/p&gt;\n\n			&lt;p&gt;Nghị định n&amp;agrave;y quy định về hoạt động b&amp;aacute;n h&amp;agrave;ng đa cấp v&amp;agrave; quản l&amp;yacute; hoạt động b&amp;aacute;n h&amp;agrave;ng đa cấp tr&amp;ecirc;n l&amp;atilde;nh thổ nước Cộng ho&amp;agrave; X&amp;atilde; hội chủ nghĩa Việt Nam.&lt;/p&gt;\n\n			&lt;p&gt;&lt;strong&gt;Điều&amp;nbsp;&lt;a name=&quot;Dieu_2&quot;&gt;&lt;/a&gt;2. Đối tượng &amp;aacute;p dụng&lt;/strong&gt;&lt;/p&gt;\n\n			&lt;p&gt;Nghị định n&amp;agrave;y &amp;aacute;p dụng đối với doanh nghiệp b&amp;aacute;n h&amp;agrave;ng đa cấp v&amp;agrave; người tham gia v&amp;agrave;o mạng lưới b&amp;aacute;n h&amp;agrave;ng đa cấp.&lt;/p&gt;\n\n			&lt;p&gt;&lt;strong&gt;Điều&amp;nbsp;&lt;a name=&quot;Dieu_3&quot;&gt;&lt;/a&gt;3. B&amp;aacute;n h&amp;agrave;ng đa cấp&lt;/strong&gt;&lt;/p&gt;\n\n			&lt;p&gt;1. B&amp;aacute;n h&amp;agrave;ng đa cấp l&amp;agrave; phương thức tiếp thị để b&amp;aacute;n lẻ h&amp;agrave;ng h&amp;oacute;a đ&amp;aacute;p ứng c&amp;aacute;c điều kiện quy định tại khoản 11 Điều 3 của Luật Cạnh tranh.&lt;/p&gt;\n\n			&lt;p&gt;2. Doanh nghiệp chỉ được tổ chức b&amp;aacute;n h&amp;agrave;ng đa cấp sau khi được cấp Giấy đăng k&amp;yacute; tổ chức b&amp;aacute;n h&amp;agrave;ng đa cấp theo quy định tại Điều 16 của Nghị định n&amp;agrave;y.&lt;/p&gt;\n\n			&lt;p&gt;&lt;strong&gt;Điều&amp;nbsp;&lt;a name=&quot;Dieu_4&quot;&gt;&lt;/a&gt;4. Người tham gia b&amp;aacute;n h&amp;agrave;ng đa cấp&lt;/strong&gt;&lt;/p&gt;\n\n			&lt;p&gt;1. Người tham gia b&amp;aacute;n h&amp;agrave;ng đa cấp (sau đ&amp;acirc;y gọi chung l&amp;agrave; người tham gia) l&amp;agrave; c&amp;aacute; nh&amp;acirc;n c&amp;oacute; năng lực h&amp;agrave;nh vi d&amp;acirc;n sự đầy đủ, đ&amp;atilde; k&amp;yacute; hợp đồng tham gia b&amp;aacute;n h&amp;agrave;ng đa cấp với doanh nghiệp b&amp;aacute;n h&amp;agrave;ng đa cấp, trừ những c&amp;aacute; nh&amp;acirc;n được quy định tại khoản 2 Điều n&amp;agrave;y.&lt;/p&gt;\n\n			&lt;p&gt;2. Những c&amp;aacute; nh&amp;acirc;n sau đ&amp;acirc;y kh&amp;ocirc;ng được tham gia b&amp;aacute;n h&amp;agrave;ng đa cấp:&lt;/p&gt;\n\n			&lt;p&gt;a) Người đang phải chấp h&amp;agrave;nh h&amp;igrave;nh phạt t&amp;ugrave; hoặc c&amp;oacute; tiền &amp;aacute;n về c&amp;aacute;c tội sản xuất, bu&amp;ocirc;n b&amp;aacute;n h&amp;agrave;ng giả, quảng c&amp;aacute;o gian dối, kinh doanh tr&amp;aacute;i ph&amp;eacute;p, trốn thuế, lừa dối kh&amp;aacute;ch h&amp;agrave;ng, c&amp;aacute;c tội về lừa đảo chiếm đoạt t&amp;agrave;i sản, lạm dụng t&amp;iacute;n nhiệm chiếm đoạt t&amp;agrave;i sản, chiếm giữ tr&amp;aacute;i ph&amp;eacute;p t&amp;agrave;i sản;&lt;/p&gt;\n\n			&lt;p&gt;b) Người nước ngo&amp;agrave;i, người Việt Nam định cư ở nước ngo&amp;agrave;i kh&amp;ocirc;ng c&amp;oacute; Giấy ph&amp;eacute;p lao động tại Việt Nam do cơ quan c&amp;oacute; thẩm quyền cấp.&lt;/p&gt;\n\n			&lt;p&gt;&lt;strong&gt;Điều&amp;nbsp;&lt;a name=&quot;Dieu_5&quot;&gt;&lt;/a&gt;5. H&amp;agrave;ng h&amp;oacute;a được kinh doanh theo phương thức b&amp;aacute;n h&amp;agrave;ng đa cấp&lt;/strong&gt;&lt;/p&gt;\n\n			&lt;p&gt;1. Tất cả h&amp;agrave;ng h&amp;oacute;a đều được kinh doanh theo phương thức b&amp;aacute;n h&amp;agrave;ng đa cấp , trừ những trường hợp sau đ&amp;acirc;y:&lt;/p&gt;\n\n			&lt;p&gt;a) H&amp;agrave;ng ho&amp;aacute; thuộc Danh mục h&amp;agrave;ng ho&amp;aacute; cấm lưu th&amp;ocirc;ng, Danh mục h&amp;agrave;ng ho&amp;aacute; hạn chế kinh doanh, h&amp;agrave;ng giả, h&amp;agrave;ng nhập lậu theo quy định của ph&amp;aacute;p luật;&lt;/p&gt;\n\n			&lt;p&gt;b) H&amp;agrave;ng ho&amp;aacute; l&amp;agrave; thuốc ph&amp;ograve;ng chữa bệnh cho người; c&amp;aacute;c loại vắc xin, sinh phẩm; trang thiết bị y tế v&amp;agrave; dụng cụ y tế; c&amp;aacute;c loại thuốc th&amp;uacute; y (bao gồm cả thuốc th&amp;uacute; y thủy sản), thuốc bảo vệ thực vật; h&amp;oacute;a chất, chế phẩm diệt c&amp;ocirc;n tr&amp;ugrave;ng, diệt khuẩn d&amp;ugrave;ng trong lĩnh vực gia dụng v&amp;agrave; y tế; nguy&amp;ecirc;n liệu l&amp;agrave;m thuốc chữa bệnh; c&amp;aacute;c loại ho&amp;aacute; chất độc hại v&amp;agrave; sản phẩm c&amp;oacute; ho&amp;aacute; chất độc hại theo quy định của ph&amp;aacute;p luật.&lt;/p&gt;\n\n			&lt;p&gt;2. H&amp;agrave;ng ho&amp;aacute; được kinh doanh theo phương thức b&amp;aacute;n h&amp;agrave;ng đa cấp phải đ&amp;aacute;p ứng c&amp;aacute;c điều kiện sau đ&amp;acirc;y:&lt;/p&gt;\n\n			&lt;p&gt;a) Đảm bảo ti&amp;ecirc;u chuẩn chất lượng, an to&amp;agrave;n, vệ sinh thực phẩm theo quy định của ph&amp;aacute;p luật;&lt;/p&gt;\n\n			&lt;p&gt;b) Đảm bảo r&amp;otilde; r&amp;agrave;ng, hợp ph&amp;aacute;p về nguồn gốc, xuất xứ, t&amp;iacute;nh năng, c&amp;ocirc;ng dụng của h&amp;agrave;ng h&amp;oacute;a;&lt;/p&gt;\n\n			&lt;p&gt;c) C&amp;oacute; nh&amp;atilde;n h&amp;agrave;ng h&amp;oacute;a theo đ&amp;uacute;ng quy định của ph&amp;aacute;p luật.&lt;/p&gt;\n\n			&lt;p&gt;&lt;strong&gt;Chương&amp;nbsp;&lt;a name=&quot;Chuong_II&quot;&gt;&lt;/a&gt;II&lt;/strong&gt;&lt;/p&gt;\n\n			&lt;p&gt;&lt;strong&gt;HOẠT ĐỘNG B&amp;Aacute;N H&amp;Agrave;NG ĐA CẤP&lt;/strong&gt;&lt;/p&gt;\n\n			&lt;p&gt;&lt;strong&gt;Điều&amp;nbsp;&lt;a name=&quot;Dieu_6&quot;&gt;&lt;/a&gt;6. Tr&amp;aacute;ch nhiệm của doanh nghiệp b&amp;aacute;n h&amp;agrave;ng đa cấp&lt;/strong&gt;&lt;/p&gt;\n\n			&lt;p&gt;1. Doanh nghiệp b&amp;aacute;n h&amp;agrave;ng đa cấp c&amp;oacute; tr&amp;aacute;ch nhiệm x&amp;acirc;y dựng v&amp;agrave; c&amp;ocirc;ng bố c&amp;ocirc;ng khai Quy tắc hoạt động của doanh nghiệp v&amp;agrave; người tham gia trong hoạt động b&amp;aacute;n h&amp;agrave;ng đa cấp.&lt;/p&gt;\n\n			&lt;p&gt;. Doanh nghiệp b&amp;aacute;n h&amp;agrave;ng đa cấp c&amp;oacute; tr&amp;aacute;ch nhiệm cung cấp t&amp;agrave;i liệu cho người c&amp;oacute; dự định tham gia mạng lưới b&amp;aacute;n h&amp;agrave;ng đa cấp của doanh nghiệp về c&amp;aacute;c nội dung sau đ&amp;acirc;y:&lt;/p&gt;\n\n			&lt;p&gt;a) Chương tr&amp;igrave;nh b&amp;aacute;n h&amp;agrave;ng, bao gồm c&amp;aacute;ch thức trả thưởng; hợp đồng mẫu m&amp;agrave; danh nghiệp sẽ k&amp;yacute; với người tham gia v&amp;agrave; mọi thoả thuận kh&amp;aacute;c quy định về quyền v&amp;agrave; nghĩa vụ của người tham gia; th&amp;ocirc;ng tin về ti&amp;ecirc;u chuẩn chất lượng hoặc chứng chỉ chất lượng (nếu c&amp;oacute;), gi&amp;aacute; cả, c&amp;ocirc;ng dụng v&amp;agrave; c&amp;aacute;ch thức sử dụng h&amp;agrave;ng h&amp;oacute;a được b&amp;aacute;n; quy định li&amp;ecirc;n quan đến bảo h&amp;agrave;nh, trả lại, mua lại h&amp;agrave;ng ho&amp;aacute; được b&amp;aacute;n;&lt;/p&gt;\n\n			&lt;p&gt;b) Chương tr&amp;igrave;nh đ&amp;agrave;o tạo người tham gia, bao gồm nội dung đ&amp;agrave;o tạo; thời gian đ&amp;agrave;o tạo; tr&amp;igrave;nh tự, thủ tục cấp chứng chỉ đ&amp;agrave;o tạo; thời gian v&amp;agrave; nội dung bồi dưỡng định kỳ cho người tham gia;&lt;/p&gt;\n\n			&lt;p&gt;c) Quy tắc hoạt động trong đ&amp;oacute; hướng dẫn c&amp;aacute;ch thức giao dịch v&amp;agrave; quy định li&amp;ecirc;n quan đến b&amp;aacute;n h&amp;agrave;ng đa cấp;&lt;/p&gt;\n\n			&lt;p&gt;d) Tr&amp;aacute;ch nhiệm của người tham gia;&lt;/p&gt;\n\n			&lt;p&gt;đ) Lợi &amp;iacute;ch kinh tế m&amp;agrave; người tham gia c&amp;oacute; thể c&amp;oacute; được bằng việc tiếp thị hay trực tiếp b&amp;aacute;n h&amp;agrave;ng ho&amp;aacute; v&amp;agrave; c&amp;aacute;c điều kiện để c&amp;oacute; được lợi &amp;iacute;ch kinh tế đ&amp;oacute;;&lt;/p&gt;\n\n			&lt;p&gt;e) Điều kiện chấm dứt hợp đồng của người tham gia v&amp;agrave; quyền, nghĩa vụ ph&amp;aacute;t sinh từ việc chấm dứt hợp đồng n&amp;agrave;y;&lt;/p&gt;\n\n			&lt;p&gt;g) C&amp;aacute;c vấn đề kh&amp;aacute;c do cơ quan nh&amp;agrave; nước c&amp;oacute; thẩm quyền quy định.&lt;/p&gt;\n\n			&lt;p&gt;3. Ngo&amp;agrave;i tr&amp;aacute;ch nhiệm quy định tại khoản 2 Điều n&amp;agrave;y, doanh nghiệp b&amp;aacute;n h&amp;agrave;ng đa cấp c&amp;ograve;n c&amp;oacute; c&amp;aacute;c tr&amp;aacute;ch nhiệm sau đ&amp;acirc;y:&lt;/p&gt;\n\n			&lt;p&gt;a) Bảo đảm t&amp;iacute;nh trung thực v&amp;agrave; độ ch&amp;iacute;nh x&amp;aacute;c của c&amp;aacute;c th&amp;ocirc;ng tin cung cấp cho người tham gia;&lt;/p&gt;\n\n			&lt;p&gt;b) Bảo đảm chất lượng của h&amp;agrave;ng h&amp;oacute;a được b&amp;aacute;n theo phương thức b&amp;aacute;n h&amp;agrave;ng đa cấp;&lt;/p&gt;\n\n			&lt;p&gt;c) Giải quyết c&amp;aacute;c khiếu nại của người tham gia v&amp;agrave; người ti&amp;ecirc;u d&amp;ugrave;ng;&lt;/p&gt;\n\n			&lt;p&gt;d) Khấu trừ tiền thuế thu nhập c&amp;aacute; nh&amp;acirc;n của người tham gia để nộp v&amp;agrave;o ng&amp;acirc;n s&amp;aacute;ch Nh&amp;agrave; nước trước khi chi trả hoa hồng, tiền thưởng hoặc lợi &amp;iacute;ch kinh tế kh&amp;aacute;c cho người tham gia;&lt;/p&gt;\n\n			&lt;p&gt;đ) Đ&amp;agrave;o tạo, bồi dưỡng nghiệp vụ b&amp;aacute;n h&amp;agrave;ng đa cấp, ph&amp;aacute;p luật về b&amp;aacute;n h&amp;agrave;ng đa cấp cho người tham gia;&lt;/p&gt;\n\n			&lt;p&gt;e) Quản l&amp;yacute; người tham gia qua hệ thống thẻ th&amp;agrave;nh vi&amp;ecirc;n mạng lưới b&amp;aacute;n h&amp;agrave;ng đa cấp theo mẫu do Bộ Thương mại quy định;&lt;/p&gt;\n\n			&lt;p&gt;g) Th&amp;ocirc;ng b&amp;aacute;o cho người tham gia những h&amp;agrave;ng ho&amp;aacute; thuộc diện kh&amp;ocirc;ng phải mua lại trước khi người đ&amp;oacute; tiến h&amp;agrave;nh mua h&amp;agrave;ng.&lt;/p&gt;\n\n			&lt;p&gt;&lt;strong&gt;Điều&amp;nbsp;&lt;a name=&quot;Dieu_7&quot;&gt;&lt;/a&gt;7. Những h&amp;agrave;nh vi bị cấm của doanh nghiệp b&amp;aacute;n h&amp;agrave;ng đa cấp&lt;/strong&gt;&lt;/p&gt;\n\n			&lt;p&gt;Cấm doanh nghiệp b&amp;aacute;n h&amp;agrave;ng đa cấp thực hiện những h&amp;agrave;nh vi sau đ&amp;acirc;y:&lt;/p&gt;\n\n			&lt;p&gt;1. Y&amp;ecirc;u cầu người muốn tham gia phải đặt cọc để được quyền tham gia mạng lưới b&amp;aacute;n h&amp;agrave;ng đa cấp.&lt;/p&gt;\n\n			&lt;p&gt;2. Y&amp;ecirc;u cầu người muốn tham gia phải mua một số lượng h&amp;agrave;ng ho&amp;aacute; ban đầu để được quyền tham gia mạng lưới b&amp;aacute;n h&amp;agrave;ng đa cấp.&lt;/p&gt;\n\n			&lt;p&gt;3. Y&amp;ecirc;u cầu người muốn tham gia phải trả tiền hoặc trả bất kỳ khoản ph&amp;iacute; n&amp;agrave;o dưới h&amp;igrave;nh thức kho&amp;aacute; học, kho&amp;aacute; đ&amp;agrave;o tạo, hội thảo, hoạt động x&amp;atilde; hội, hay c&amp;aacute;c hoạt động tương tự kh&amp;aacute;c để được quyền tham gia mạng lưới b&amp;aacute;n h&amp;agrave;ng đa cấp trừ tiền mua t&amp;agrave;i liệu theo quy định tại khoản 2 Điều 6 của Nghị định n&amp;agrave;y.&lt;/p&gt;\n\n			&lt;p&gt;4. Kh&amp;ocirc;ng cam kết cho người tham gia trả lại h&amp;agrave;ng ho&amp;aacute; v&amp;agrave; nhận lại khoản tiền đ&amp;atilde; chuyển cho doanh nghiệp theo quy định tại Điều 11 của Nghị định n&amp;agrave;y.&lt;/p&gt;\n\n			&lt;p&gt;5. Cản trở người tham gia trả lại h&amp;agrave;ng ho&amp;aacute; ph&amp;aacute;t sinh từ việc chấm dứt hợp đồng tham gia b&amp;aacute;n h&amp;agrave;ng đa cấp.&lt;/p&gt;\n\n			&lt;p&gt;6. Cho người tham gia nhận tiền hoa hồng, tiền thưởng, lợi &amp;iacute;ch kinh tế kh&amp;aacute;c từ việc dụ dỗ người kh&amp;aacute;c tham gia b&amp;aacute;n h&amp;agrave;ng đa cấp.&lt;/p&gt;\n\n			&lt;p&gt;7. Từ chối chi trả kh&amp;ocirc;ng c&amp;oacute; l&amp;yacute; do ch&amp;iacute;nh đ&amp;aacute;ng c&amp;aacute;c khoản hoa hồng, tiền thưởng hay c&amp;aacute;c lợi &amp;iacute;ch kinh tế kh&amp;aacute;c m&amp;agrave; người tham gia c&amp;oacute; quyền hưởng.&lt;/p&gt;\n\n			&lt;p&gt;8. Cung cấp th&amp;ocirc;ng tin gian dối về lợi &amp;iacute;ch của việc tham gia mạng lưới b&amp;aacute;n h&amp;agrave;ng đa cấp để dụ dỗ người kh&amp;aacute;c tham gia b&amp;aacute;n h&amp;agrave;ng đa cấp.&lt;/p&gt;\n\n			&lt;p&gt;9. Cung cấp th&amp;ocirc;ng tin sai lệch về t&amp;iacute;nh chất, c&amp;ocirc;ng dụng của h&amp;agrave;ng ho&amp;aacute; để dụ dỗ người kh&amp;aacute;c tham gia b&amp;aacute;n h&amp;agrave;ng đa cấp.&lt;/p&gt;\n\n			&lt;p&gt;&lt;strong&gt;Điều&amp;nbsp;&lt;a name=&quot;Dieu_8&quot;&gt;&lt;/a&gt;8. Tr&amp;aacute;ch nhiệm v&amp;agrave; những h&amp;agrave;nh vi bị cấm của người tham gia&lt;/strong&gt;&lt;/p&gt;\n\n			&lt;p&gt;1. Khi tham gia hoạt động b&amp;aacute;n h&amp;agrave;ng đa cấp người tham gia c&amp;oacute; những tr&amp;aacute;ch nhiệm sau đ&amp;acirc;y:&lt;/p&gt;\n\n			&lt;p&gt;a) Xuất tr&amp;igrave;nh thẻ th&amp;agrave;nh vi&amp;ecirc;n mạng lưới b&amp;aacute;n h&amp;agrave;ng đa cấp trước khi giới thiệu h&amp;agrave;ng ho&amp;aacute; hoặc tiếp thị b&amp;aacute;n h&amp;agrave;ng;&lt;/p&gt;\n\n			&lt;p&gt;b) Th&amp;ocirc;ng b&amp;aacute;o đầy đủ những nội dung quy định tại khoản 2 Điều 6 của Nghị định n&amp;agrave;y khi bảo trợ một người kh&amp;aacute;c tham gia v&amp;agrave;o mạng lưới b&amp;aacute;n h&amp;agrave;ng đa cấp;&lt;/p&gt;\n\n			&lt;p&gt;c) Đưa tin trung thực, ch&amp;iacute;nh x&amp;aacute;c về loại, chất lượng, gi&amp;aacute; cả, c&amp;ocirc;ng dụng, c&amp;aacute;ch thức sử dụng của h&amp;agrave;ng ho&amp;aacute; được b&amp;aacute;n;&lt;/p&gt;\n\n			&lt;p&gt;d) Tu&amp;acirc;n thủ quy định trong Quy tắc hoạt động v&amp;agrave; Chương tr&amp;igrave;nh b&amp;aacute;n h&amp;agrave;ng của doanh nghiệp.&lt;/p&gt;\n\n			&lt;p&gt;2. Cấm người tham gia thực hiện những h&amp;agrave;nh vi sau đ&amp;acirc;y:&lt;/p&gt;\n\n			&lt;p&gt;a) Y&amp;ecirc;u cầu người được m&amp;igrave;nh bảo trợ tham gia v&amp;agrave;o mạng lưới b&amp;aacute;n h&amp;agrave;ng đa cấp trả bất kỳ khoản ph&amp;iacute; n&amp;agrave;o dưới danh nghĩa kho&amp;aacute; học, kho&amp;aacute; đ&amp;agrave;o tạo, hội thảo, hoạt động x&amp;atilde; hội, hay c&amp;aacute;c hoạt động tương tự kh&amp;aacute;c;&lt;/p&gt;\n\n			&lt;p&gt;b) Cung cấp th&amp;ocirc;ng tin gian dối về lợi &amp;iacute;ch của việc tham gia b&amp;aacute;n h&amp;agrave;ng đa cấp, th&amp;ocirc;ng tin sai lệch về t&amp;iacute;nh chất, c&amp;ocirc;ng dụng của h&amp;agrave;ng ho&amp;aacute;, về hoạt động của doanh nghiệp b&amp;aacute;n h&amp;agrave;ng đa cấp để dụ dỗ người kh&amp;aacute;c tham gia b&amp;aacute;n h&amp;agrave;ng đa cấp.&lt;/p&gt;\n\n			&lt;p&gt;&lt;strong&gt;Điều&amp;nbsp;&lt;a name=&quot;Dieu_9&quot;&gt;&lt;/a&gt;9. Hợp đồng tham gia b&amp;aacute;n h&amp;agrave;ng đa cấp&lt;/strong&gt;&lt;/p&gt;\n\n			&lt;p&gt;1. Doanh nghiệp b&amp;aacute;n h&amp;agrave;ng đa cấp phải k&amp;yacute; kết hợp đồng tham gia bằng văn bản với người tham gia v&amp;agrave;o mạng lưới b&amp;aacute;n h&amp;agrave;ng đa cấp.&lt;/p&gt;\n\n			&lt;p&gt;2. Bộ Thương mại chịu tr&amp;aacute;ch nhiệm hướng dẫn về c&amp;aacute;c nội dung cơ bản của hợp đồng mẫu cho hoạt động b&amp;aacute;n h&amp;agrave;ng đa cấp.&lt;/p&gt;\n\n			&lt;p&gt;&lt;strong&gt;Điều&amp;nbsp;&lt;a name=&quot;Dieu_10&quot;&gt;&lt;/a&gt;10. Chấm dứt hợp đồng tham gia b&amp;aacute;n h&amp;agrave;ng đa cấp&lt;/strong&gt;&lt;/p&gt;\n\n			&lt;p&gt;1. Người tham gia c&amp;oacute; quyền chấm dứt hợp đồng tham gia bằng việc gửi cho doanh nghiệp b&amp;aacute;n h&amp;agrave;ng đa cấp một th&amp;ocirc;ng b&amp;aacute;o bằng văn bản trước khi chấm dứt hợp đồng tối thiểu l&amp;agrave; 7 ng&amp;agrave;y l&amp;agrave;m việc.&lt;/p&gt;\n\n			&lt;p&gt;2. Doanh nghiệp b&amp;aacute;n h&amp;agrave;ng đa cấp c&amp;oacute; quyền chấm dứt hợp đồng với người tham gia khi người tham gia vi phạm c&amp;aacute;c quy định tại Điều 8 của Nghị định n&amp;agrave;y v&amp;agrave; phải th&amp;ocirc;ng b&amp;aacute;o cho người tham gia biết bằng văn bản trước khi chấm dứt hợp đồng tối thiểu l&amp;agrave; 7 ng&amp;agrave;y l&amp;agrave;m việc.&lt;/p&gt;\n\n			&lt;p&gt;3. Trong thời hạn 15 ng&amp;agrave;y l&amp;agrave;m việc kể từ ng&amp;agrave;y chấm dứt hợp đồng, doanh nghiệp b&amp;aacute;n h&amp;agrave;ng đa cấp c&amp;oacute; tr&amp;aacute;ch nhiệm sau đ&amp;acirc;y:&lt;/p&gt;\n\n			&lt;p&gt;a) Mua lại từ người tham gia h&amp;agrave;ng ho&amp;aacute; đ&amp;atilde; b&amp;aacute;n cho người tham gia theo quy định tại Điều 11 của Nghị định n&amp;agrave;y;&lt;/p&gt;\n\n			&lt;p&gt;b) Thanh to&amp;aacute;n cho người tham gia tiền hoa hồng, tiền thưởng v&amp;agrave; c&amp;aacute;c lợi &amp;iacute;ch kinh tế m&amp;agrave; người tham gia c&amp;oacute; quyền nhận trong qu&amp;aacute; tr&amp;igrave;nh tham gia v&amp;agrave;o mạng lưới b&amp;aacute;n h&amp;agrave;ng đa cấp.&lt;/p&gt;\n\n			&lt;p&gt;&lt;strong&gt;Điều&amp;nbsp;&lt;a name=&quot;Dieu_11&quot;&gt;&lt;/a&gt;11. Mua lại h&amp;agrave;ng ho&amp;aacute; của người tham gia khi chấm dứt hợp đồng tham gia b&amp;aacute;n h&amp;agrave;ng đa cấp&lt;/strong&gt;&lt;/p&gt;\n\n			&lt;p&gt;1. Doanh nghiệp b&amp;aacute;n h&amp;agrave;ng đa cấp phải mua lại h&amp;agrave;ng ho&amp;aacute; đ&amp;atilde; b&amp;aacute;n cho người tham gia khi h&amp;agrave;ng ho&amp;aacute; đ&amp;oacute; đ&amp;aacute;p ứng c&amp;aacute;c điều kiện sau đ&amp;acirc;y:&lt;/p&gt;\n\n			&lt;p&gt;a) C&amp;oacute; thể b&amp;aacute;n lại theo mục đ&amp;iacute;ch sử dụng ban đầu của h&amp;agrave;ng ho&amp;aacute;;&lt;/p&gt;\n\n			&lt;p&gt;b) Trong thời hạn kh&amp;ocirc;ng qu&amp;aacute; 30 ng&amp;agrave;y kể từ ng&amp;agrave;y người tham gia nhận h&amp;agrave;ng.&lt;/p&gt;\n\n			&lt;p&gt;2. Trong trường hợp phải mua lại h&amp;agrave;ng ho&amp;aacute; theo quy định tại khoản 1 Điều n&amp;agrave;y, doanh nghiệp b&amp;aacute;n h&amp;agrave;ng đa cấp c&amp;oacute; tr&amp;aacute;ch nhiệm:&lt;/p&gt;\n\n			&lt;p&gt;a) Ho&amp;agrave;n lại tổng số tiền m&amp;agrave; người tham gia đ&amp;atilde; trả để nhận được h&amp;agrave;ng ho&amp;aacute; đ&amp;oacute; nếu kh&amp;ocirc;ng c&amp;oacute; căn cứ để khấu trừ theo quy định tại điểm b khoản n&amp;agrave;y;&lt;/p&gt;\n\n			&lt;p&gt;b) Trong trường hợp doanh nghiệp phải chịu c&amp;aacute;c chi ph&amp;iacute; quản l&amp;yacute;, t&amp;aacute;i lưu kho v&amp;agrave; c&amp;aacute;c chi ph&amp;iacute; h&amp;agrave;nh ch&amp;iacute;nh kh&amp;aacute;c th&amp;igrave; phải ho&amp;agrave;n lại tổng số tiền kh&amp;ocirc;ng &amp;iacute;t hơn 90% khoản tiền m&amp;agrave; người tham gia đ&amp;atilde; trả để nhận được h&amp;agrave;ng ho&amp;aacute; đ&amp;oacute;.&lt;/p&gt;\n\n			&lt;p&gt;3. Khi ho&amp;agrave;n lại tiền theo quy định tại khoản 2 Điều n&amp;agrave;y, doanh nghiệp b&amp;aacute;n h&amp;agrave;ng đa cấp c&amp;oacute; thể khấu trừ tiền hoa hồng, tiền thưởng v&amp;agrave;/hoặc lợi &amp;iacute;ch kinh tế kh&amp;aacute;c m&amp;agrave; người tham gia đ&amp;atilde; nhận từ việc nhận h&amp;agrave;ng ho&amp;aacute; đ&amp;oacute;.&lt;/p&gt;\n\n			&lt;p&gt;4. Quy định tại khoản 1 v&amp;agrave; khoản 2 Điều n&amp;agrave;y kh&amp;ocirc;ng &amp;aacute;p dụng đối với trường hợp h&amp;agrave;ng ho&amp;aacute; thuộc diện kh&amp;ocirc;ng phải mua lại bao gồm: h&amp;agrave;ng ho&amp;aacute; khi trả lại đ&amp;atilde; hết hạn sử dụng, h&amp;agrave;ng ho&amp;aacute; theo m&amp;ugrave;a hoặc h&amp;agrave;ng ho&amp;aacute; d&amp;ugrave;ng để khuyến mại.&lt;/p&gt;\n\n			&lt;p&gt;&lt;strong&gt;Điều&amp;nbsp;&lt;a name=&quot;Dieu_12&quot;&gt;&lt;/a&gt;12. R&amp;agrave;ng buộc tr&amp;aacute;ch nhiệm giữa doanh nghiệp b&amp;aacute;n h&amp;agrave;ng đa cấp v&amp;agrave; người tham gia&lt;/strong&gt;&lt;/p&gt;\n\n			&lt;p&gt;1. Doanh nghiệp b&amp;aacute;n h&amp;agrave;ng đa cấp c&amp;oacute; tr&amp;aacute;ch nhiệm bồi thường cho người ti&amp;ecirc;u d&amp;ugrave;ng hoặc người tham gia trong c&amp;aacute;c trường hợp sau đ&amp;acirc;y:&lt;/p&gt;\n\n			&lt;p&gt;a) Người tham gia g&amp;acirc;y thiệt hại cho người ti&amp;ecirc;u d&amp;ugrave;ng hoặc người tham gia kh&amp;aacute;c khi thực hiện đ&amp;uacute;ng Quy chế hoạt động v&amp;agrave; Chương tr&amp;igrave;nh b&amp;aacute;n h&amp;agrave;ng của doanh nghiệp;&lt;/p&gt;\n\n			&lt;p&gt;b) Người tham gia kh&amp;ocirc;ng được th&amp;ocirc;ng tin đầy đủ về h&amp;agrave;ng h&amp;oacute;a theo quy định tại khoản 2 Điều 6 của Nghị định n&amp;agrave;y.&lt;/p&gt;\n\n			&lt;p&gt;2. Doanh nghiệp b&amp;aacute;n h&amp;agrave;ng đa cấp c&amp;oacute; tr&amp;aacute;ch nhiệm thường xuy&amp;ecirc;n gi&amp;aacute;m s&amp;aacute;t hoạt động của người tham gia để bảo đảm người tham gia thực hiện đ&amp;uacute;ng Quy tắc hoạt động v&amp;agrave; Chương tr&amp;igrave;nh b&amp;aacute;n h&amp;agrave;ng của doanh nghiệp.&lt;/p&gt;\n\n			&lt;p&gt;3. Trường hợp kh&amp;ocirc;ng tu&amp;acirc;n thủ c&amp;aacute;c quy định tại Điều 8 của Nghị định n&amp;agrave;y v&amp;agrave; g&amp;acirc;y thiệt hại cho người ti&amp;ecirc;u d&amp;ugrave;ng hoặc người tham gia kh&amp;aacute;c, người tham gia b&amp;aacute;n h&amp;agrave;ng đa cấp c&amp;oacute; tr&amp;aacute;ch nhiệm bồi thường thiệt hại đ&amp;atilde; g&amp;acirc;y ra.&lt;/p&gt;\n\n			&lt;p&gt;&lt;strong&gt;Điều&amp;nbsp;&lt;a name=&quot;Dieu_13&quot;&gt;&lt;/a&gt;13. Th&amp;ocirc;ng tin về c&amp;aacute;c lợi &amp;iacute;ch khi tham gia b&amp;aacute;n h&amp;agrave;ng đa cấp&lt;/strong&gt;&lt;/p&gt;\n\n			&lt;p&gt;Trường hợp sử dụng một c&amp;aacute; nh&amp;acirc;n tham gia mạng lưới b&amp;aacute;n h&amp;agrave;ng đa cấp để giới thiệu hoạt động b&amp;aacute;n h&amp;agrave;ng, doanh nghiệp b&amp;aacute;n h&amp;agrave;ng đa cấp hoặc người tham gia phải n&amp;ecirc;u r&amp;otilde; t&amp;ecirc;n, tuổi, địa chỉ, thời gian tham gia, lợi nhuận thu được từng kỳ c&amp;oacute; bi&amp;ecirc;n lai x&amp;aacute;c nhận của cơ quan thuế đ&amp;atilde; thu thuế của người đ&amp;oacute;.&lt;/p&gt;\n\n			&lt;p&gt;&lt;strong&gt;Chương&amp;nbsp;&lt;a name=&quot;Chuong_III&quot;&gt;&lt;/a&gt;III&lt;/strong&gt;&lt;/p&gt;\n\n			&lt;p&gt;&lt;strong&gt;QUẢN L&amp;Yacute; HOẠT ĐỘNG B&amp;Aacute;N H&amp;Agrave;NG ĐA CẤP&lt;/strong&gt;&lt;/p&gt;\n\n			&lt;p&gt;&lt;strong&gt;Điều&amp;nbsp;&lt;a name=&quot;Dieu_14&quot;&gt;&lt;/a&gt;14. Điều kiện cấp Giấy đăng k&amp;yacute; tổ chức b&amp;aacute;n h&amp;agrave;ng đa cấp&lt;/strong&gt;&lt;/p&gt;\n\n			&lt;p&gt;Doanh nghiệp được cấp Giấy đăng k&amp;yacute; tổ chức b&amp;aacute;n h&amp;agrave;ng đa cấp khi đ&amp;aacute;p ứng đủ những điều kiện sau đ&amp;acirc;y:&lt;/p&gt;\n\n			&lt;p&gt;1. Đ&amp;atilde; thực hiện k&amp;yacute; quỹ theo quy định tại Điều 17 của Nghị định n&amp;agrave;y.&lt;/p&gt;\n\n			&lt;p&gt;2. Kinh doanh h&amp;agrave;ng h&amp;oacute;a ph&amp;ugrave; hợp với ng&amp;agrave;nh nghề ghi trong Giấy chứng nhận đăng k&amp;yacute; kinh doanh của doanh nghiệp.&lt;/p&gt;\n\n			&lt;p&gt;3. C&amp;oacute; đủ điều kiện kinh doanh hoặc được cấp Giấy chứng nhận đủ điều kiện kinh doanh theo quy định của ph&amp;aacute;p luật trong trường hợp kinh doanh h&amp;agrave;ng h&amp;oacute;a thuộc Danh mục h&amp;agrave;ng h&amp;oacute;a kinh doanh c&amp;oacute; điều kiện.&lt;/p&gt;\n\n			&lt;p&gt;4. C&amp;oacute; Chương tr&amp;igrave;nh b&amp;aacute;n h&amp;agrave;ng minh bạch v&amp;agrave; kh&amp;ocirc;ng tr&amp;aacute;i ph&amp;aacute;p luật.&lt;/p&gt;\n\n			&lt;p&gt;5. C&amp;oacute; Chương tr&amp;igrave;nh đ&amp;agrave;o tạo người tham gia r&amp;otilde; r&amp;agrave;ng.&lt;/p&gt;\n\n			&lt;p&gt;&lt;strong&gt;Điều&amp;nbsp;&lt;a name=&quot;Dieu_15&quot;&gt;&lt;/a&gt;15. Hồ sơ đề nghị cấp Giấy đăng k&amp;yacute; tổ chức b&amp;aacute;n h&amp;agrave;ng đa cấp&lt;/strong&gt;&lt;/p&gt;\n\n			&lt;p&gt;Hồ sơ đề nghị cấp Giấy đăng k&amp;yacute; tổ chức b&amp;aacute;n h&amp;agrave;ng đa cấp được nộp tại Sở Thương mại hoặc Sở Thương mại Du lịch cấp tỉnh nơi doanh nghiệp đăng k&amp;yacute; kinh doanh. Hồ sơ bao gồm:&lt;/p&gt;\n\n			&lt;p&gt;1. Đơn đề nghị cấp Giấy đăng k&amp;yacute; tổ chức b&amp;aacute;n h&amp;agrave;ng đa cấp theo mẫu của Bộ Thương mại.&lt;/p&gt;\n\n			&lt;p&gt;2. Bản sao c&amp;oacute; c&amp;ocirc;ng chứng Giấy chứng nhận đăng k&amp;yacute; kinh doanh.&lt;/p&gt;\n\n			&lt;p&gt;3. Văn bản x&amp;aacute;c nhận của ng&amp;acirc;n h&amp;agrave;ng về số tiền k&amp;yacute; quỹ theo quy định tại khoản 1 Điều 17 của Nghị định n&amp;agrave;y.&lt;/p&gt;\n\n			&lt;p&gt;4. Bản sao c&amp;oacute; c&amp;ocirc;ng chứng Giấy chứng nhận đủ điều kiện kinh doanh trong trường hợp kinh doanh h&amp;agrave;ng h&amp;oacute;a thuộc Danh mục h&amp;agrave;ng h&amp;oacute;a kinh doanh c&amp;oacute; điều kiện.&lt;/p&gt;\n\n			&lt;p&gt;5. Danh s&amp;aacute;ch v&amp;agrave; l&amp;yacute; lịch của những người đứng đầu doanh nghiệp c&amp;oacute; ảnh v&amp;agrave; x&amp;aacute;c nhận của c&amp;ocirc;ng an x&amp;atilde;, phường nơi cư tr&amp;uacute;. Đối với người nước ngo&amp;agrave;i, phải c&amp;oacute; x&amp;aacute;c nhận của Đại sứ qu&amp;aacute;n hoặc cơ quan l&amp;atilde;nh sự tại Việt Nam của nước người đ&amp;oacute; mang quốc tịch.&lt;/p&gt;\n\n			&lt;p&gt;6. Chương tr&amp;igrave;nh b&amp;aacute;n h&amp;agrave;ng c&amp;oacute; c&amp;aacute;c nội dung quy định tại điểm a khoản 2 Điều 6 của Nghị định n&amp;agrave;y.&lt;/p&gt;\n\n			&lt;p&gt;7. Chương tr&amp;igrave;nh đ&amp;agrave;o tạo người tham gia c&amp;oacute; c&amp;aacute;c nội dung quy định tại điểm b khoản 2 Điều 6 của Nghị định n&amp;agrave;y.&lt;/p&gt;\n\n			&lt;p&gt;&lt;strong&gt;Điều&amp;nbsp;&lt;a name=&quot;Dieu_16&quot;&gt;&lt;/a&gt;16. Thủ tục cấp, bổ sung Giấy đăng k&amp;yacute; tổ chức b&amp;aacute;n h&amp;agrave;ng đa cấp&lt;/strong&gt;&lt;/p&gt;\n\n			&lt;p&gt;1. Trong thời hạn 15 ng&amp;agrave;y l&amp;agrave;m việc, kể từ ng&amp;agrave;y nhận được hồ sơ đầy đủ, hợp lệ, Sở Thương mại hoặc Sở Thương mại Du lịch cấp tỉnh nơi doanh nghiệp đăng k&amp;yacute; kinh doanh c&amp;oacute; tr&amp;aacute;ch nhiệm cấp Giấy đăng k&amp;yacute; tổ chức b&amp;aacute;n h&amp;agrave;ng đa cấp cho doanh nghiệp khi hồ sơ của doanh nghiệp đ&amp;aacute;p ứng đầy đủ c&amp;aacute;c điều kiện quy định tại Điều 14 của Nghị định n&amp;agrave;y.&lt;/p&gt;\n\n			&lt;p&gt;Trường hợp kh&amp;ocirc;ng cấp Giấy đăng k&amp;yacute; tổ chức b&amp;aacute;n h&amp;agrave;ng đa cấp, Sở Thương mại hoặc Sở Thương mại - Du lịch phải trả lời bằng văn bản v&amp;agrave; n&amp;ecirc;u r&amp;otilde; l&amp;yacute; do.&lt;/p&gt;\n\n			&lt;p&gt;2. Doanh nghiệp đề nghị cấp Giấy đăng k&amp;yacute; tổ chức b&amp;aacute;n h&amp;agrave;ng đa cấp phải nộp lệ ph&amp;iacute; cấp Giấy đăng k&amp;yacute; tổ chức b&amp;aacute;n h&amp;agrave;ng đa cấp. Mức thu lệ ph&amp;iacute; v&amp;agrave; chế độ quản l&amp;yacute;, sử dụng lệ ph&amp;iacute; do Bộ T&amp;agrave;i ch&amp;iacute;nh quy định.&lt;/p&gt;\n\n			&lt;p&gt;3. Trường hợp c&amp;oacute; những thay đổi li&amp;ecirc;n quan đến nội dung của Chương tr&amp;igrave;nh b&amp;aacute;n h&amp;agrave;ng th&amp;igrave; doanh nghiệp c&amp;oacute; tr&amp;aacute;ch nhiệm l&amp;agrave;m thủ tục đề nghị cấp bổ sung Giấy đăng k&amp;yacute; tổ chức b&amp;aacute;n h&amp;agrave;ng đa cấp.&lt;/p&gt;\n\n			&lt;p&gt;Tr&amp;igrave;nh tự, thời hạn cấp bổ sung Giấy đăng k&amp;yacute; tổ chức b&amp;aacute;n h&amp;agrave;ng đa cấp thực hiện theo quy định tại khoản 1 v&amp;agrave; khoản 2 Điều n&amp;agrave;y.&lt;/p&gt;\n\n			&lt;p&gt;4. Trong thời hạn 15 ng&amp;agrave;y l&amp;agrave;m việc, kể từ ng&amp;agrave;y cấp hoặc cấp bổ sung Giấy đăng k&amp;yacute; tổ chức b&amp;aacute;n h&amp;agrave;ng đa cấp, Sở Thương mại hoặc Sở Thương mại - Du lịch c&amp;oacute; tr&amp;aacute;ch nhiệm b&amp;aacute;o c&amp;aacute;o bằng văn bản tới Cơ quan quản l&amp;yacute; cạnh tranh thuộc Bộ Thương mại.&lt;/p&gt;\n\n			&lt;p&gt;5. Khi doanh nghiệp b&amp;aacute;n h&amp;agrave;ng đa cấp ph&amp;aacute;t triển mạng lưới b&amp;aacute;n h&amp;agrave;ng ra c&amp;aacute;c tỉnh, th&amp;agrave;nh phố trực thuộc Trung ương m&amp;agrave; doanh nghiệp kh&amp;ocirc;ng đặt trụ sở ch&amp;iacute;nh, doanh nghiệp phải th&amp;ocirc;ng b&amp;aacute;o cho Sở Thương mại, Sở Thương mại - Du lịch của tỉnh, th&amp;agrave;nh phố trực thuộc Trung ương đ&amp;oacute;.&lt;/p&gt;\n\n			&lt;p&gt;6. Bộ Thương mại quy định mẫu Giấy đăng k&amp;yacute; tổ chức b&amp;aacute;n h&amp;agrave;ng đa cấp v&amp;agrave; mẫu Th&amp;ocirc;ng b&amp;aacute;o tổ chức b&amp;aacute;n h&amp;agrave;ng đa cấp.&lt;/p&gt;\n\n			&lt;p&gt;&lt;strong&gt;Điều&amp;nbsp;&lt;a name=&quot;Dieu_17&quot;&gt;&lt;/a&gt;17. K&amp;yacute; quỹ&lt;/strong&gt;&lt;/p&gt;\n\n			&lt;p&gt;1. Doanh nghiệp b&amp;aacute;n h&amp;agrave;ng đa cấp phải k&amp;yacute; quỹ 5% vốn điều lệ nhưng kh&amp;ocirc;ng thấp hơn 1 tỷ đồng Việt Nam tại một ng&amp;acirc;n h&amp;agrave;ng thương mại hoạt động tại Việt Nam.&lt;/p&gt;\n\n			&lt;p&gt;2. Khi c&amp;oacute; th&amp;ocirc;ng b&amp;aacute;o ngừng hoạt động b&amp;aacute;n h&amp;agrave;ng đa cấp, doanh nghiệp b&amp;aacute;n h&amp;agrave;ng đa cấp được sử dụng tiền k&amp;yacute; quỹ để chi trả tiền hoa hồng, tiền thưởng hoặc tiền mua lại h&amp;agrave;ng h&amp;oacute;a từ người tham gia.&lt;/p&gt;\n\n			&lt;p&gt;3. Khi chấm dứt hoạt động b&amp;aacute;n h&amp;agrave;ng đa cấp, doanh nghiệp b&amp;aacute;n h&amp;agrave;ng đa cấp chỉ được r&amp;uacute;t to&amp;agrave;n bộ số tiền k&amp;yacute; quỹ trong trường hợp kh&amp;ocirc;ng c&amp;oacute; bất cứ khiếu kiện n&amp;agrave;o từ ph&amp;iacute;a người tham gia b&amp;aacute;n h&amp;agrave;ng đa cấp li&amp;ecirc;n quan đến việc chi trả hoa hồng, tiền thưởng hoặc tiền mua lại h&amp;agrave;ng h&amp;oacute;a.&lt;/p&gt;\n\n			&lt;p&gt;&lt;strong&gt;Điều&amp;nbsp;&lt;a name=&quot;Dieu_18&quot;&gt;&lt;/a&gt;18. Thu hồi Giấy đăng k&amp;yacute; tổ chức b&amp;aacute;n h&amp;agrave;ng đa cấp&lt;/strong&gt;&lt;/p&gt;\n\n			&lt;p&gt;1. Sở Thương mại hoặc Sở Thương mại - Du lịch thu hồi Giấy đăng k&amp;yacute; tổ chức b&amp;aacute;n h&amp;agrave;ng đa cấp trong những trường hợp sau đ&amp;acirc;y:&lt;/p&gt;\n\n			&lt;p&gt;a) Doanh nghiệp bị thu hồi Giấy chứng nhận đăng k&amp;yacute; kinh doanh;&lt;/p&gt;\n\n			&lt;p&gt;b) Doanh nghiệp bị thu hồi Giấy chứng nhận đủ điều kiện kinh doanh trong trường hợp kinh doanh h&amp;agrave;ng h&amp;oacute;a thuộc Danh mục h&amp;agrave;ng h&amp;oacute;a kinh doanh c&amp;oacute; điều kiện;&lt;/p&gt;\n\n			&lt;p&gt;c) Hồ sơ xin cấp Giấy đăng k&amp;yacute; tổ chức b&amp;aacute;n h&amp;agrave;ng đa cấp c&amp;oacute; th&amp;ocirc;ng tin gian dối do cố &amp;yacute;;&lt;/p&gt;\n\n			&lt;p&gt;d) C&amp;oacute; h&amp;agrave;nh vi vi phạm ph&amp;aacute;p luật nghi&amp;ecirc;m trọng trong hoạt động b&amp;aacute;n h&amp;agrave;ng đa cấp.&lt;/p&gt;\n\n			&lt;p&gt;2. Trong trường hợp bị thu hồi Giấy đăng k&amp;yacute; tổ chức b&amp;aacute;n h&amp;agrave;ng đa cấp theo quy định khoản 1 Điều n&amp;agrave;y, doanh nghiệp b&amp;aacute;n h&amp;agrave;ng đa cấp phải đ&amp;igrave;nh chỉ ngay việc b&amp;aacute;n h&amp;agrave;ng đa cấp, đ&amp;igrave;nh chỉ việc tuyển dụng người tham gia mới v&amp;agrave; phải thực hiện tr&amp;aacute;ch nhiệm đối với người tham gia theo quy định tại Điều 11 của Nghị định n&amp;agrave;y hoặc bồi thường cho người ti&amp;ecirc;u d&amp;ugrave;ng, người tham gia đối với c&amp;aacute;c vụ việc ph&amp;aacute;t sinh từ những giao kết trước ng&amp;agrave;y bị thu hồi Giấy đăng k&amp;yacute; tổ chức b&amp;aacute;n h&amp;agrave;ng đa cấp.&lt;/p&gt;\n\n			&lt;p&gt;3. Quyết định thu hồi Giấy đăng k&amp;yacute; tổ chức b&amp;aacute;n h&amp;agrave;ng đa cấp được Sở Thương mại hoặc Sở Thương mại - Du lịch gửi tới Cơ quan quản l&amp;yacute; cạnh tranh thuộc Bộ Thương mại v&amp;agrave; c&amp;ocirc;ng bố tr&amp;ecirc;n c&amp;aacute;c phương tiện th&amp;ocirc;ng tin đại ch&amp;uacute;ng.&lt;/p&gt;\n\n			&lt;p&gt;&lt;strong&gt;Điều&amp;nbsp;&lt;a name=&quot;Dieu_19&quot;&gt;&lt;/a&gt;19. Tạm ngừng, chấm dứt hoạt động b&amp;aacute;n h&amp;agrave;ng đa cấp&lt;/strong&gt;&lt;/p&gt;\n\n			&lt;p&gt;1. Khi muốn tạm ngừng hoặc chấm dứt hoạt động b&amp;aacute;n h&amp;agrave;ng đa cấp, doanh nghiệp b&amp;aacute;n h&amp;agrave;ng đa cấp c&amp;oacute; nghĩa vụ sau đ&amp;acirc;y:&lt;/p&gt;\n\n			&lt;p&gt;a) Tu&amp;acirc;n thủ c&amp;aacute;c quy định về tạm ngừng hoặc chấm dứt hoạt động kinh doanh theo quy định của ph&amp;aacute;p luật về doanh nghiệp;&lt;/p&gt;\n\n			&lt;p&gt;b) Th&amp;ocirc;ng b&amp;aacute;o với Sở Thương mại hoặc Sở Thương mại - Du lịch cấp tỉnh nơi doanh nghiệp đăng k&amp;yacute; kinh doanh đồng thời th&amp;ocirc;ng b&amp;aacute;o c&amp;ocirc;ng khai ở trụ sở ch&amp;iacute;nh v&amp;agrave; th&amp;ocirc;ng b&amp;aacute;o cho những người tham gia biết trong thời hạn 30 ng&amp;agrave;y l&amp;agrave;m việc trước khi tạm ngừng hoặc chấm dứt hoạt động.&lt;/p&gt;\n\n			&lt;p&gt;2. Trong trường hợp tạm ngừng hoặc chấm dứt hoạt động b&amp;aacute;n h&amp;agrave;ng đa cấp, doanh nghiệp b&amp;aacute;n h&amp;agrave;ng đa cấp phải tiến h&amp;agrave;nh thanh l&amp;yacute; hợp đồng tham gia b&amp;aacute;n h&amp;agrave;ng đa cấp với người tham gia theo quy định tại Điều 10 của Nghị định n&amp;agrave;y trong thời hạn 30 ng&amp;agrave;y l&amp;agrave;m việc kể từ ng&amp;agrave;y tạm ngừng hoặc chấm dứt hoạt động b&amp;aacute;n h&amp;agrave;ng đa cấp.&lt;/p&gt;\n\n			&lt;p&gt;&lt;strong&gt;Điều&amp;nbsp;&lt;a name=&quot;Dieu_20&quot;&gt;&lt;/a&gt;20. B&amp;aacute;o c&amp;aacute;o định kỳ của doanh nghiệp b&amp;aacute;n h&amp;agrave;ng đa cấp&lt;/strong&gt;&lt;/p&gt;\n\n			&lt;p&gt;Định kỳ 6 th&amp;aacute;ng doanh nghiệp b&amp;aacute;n h&amp;agrave;ng đa cấp c&amp;oacute; tr&amp;aacute;ch nhiệm b&amp;aacute;o c&amp;aacute;o với Sở Thương mại hoặc Sở Thương mại - Du lịch cấp tỉnh nơi doanh nghiệp đăng k&amp;yacute; kinh doanh về số lượng người tham gia, doanh thu v&amp;agrave; số thuế đ&amp;atilde; nộp của doanh nghiệp, thuế thu nhập c&amp;aacute; nh&amp;acirc;n của người tham gia m&amp;agrave; doanh nghiệp đ&amp;atilde; nộp hộ.&lt;/p&gt;\n\n			&lt;p&gt;&lt;strong&gt;Điều&amp;nbsp;&lt;a name=&quot;Dieu_21&quot;&gt;&lt;/a&gt;21. Tr&amp;aacute;ch nhiệm của Bộ Thương mại&lt;/strong&gt;&lt;/p&gt;\n\n			&lt;p&gt;1. Bộ Thương mại chịu tr&amp;aacute;ch nhiệm trước Ch&amp;iacute;nh phủ thực hiện chức năng quản l&amp;yacute; nh&amp;agrave; nước đối với hoạt động b&amp;aacute;n h&amp;agrave;ng đa cấp trong phạm vi cả nước.&lt;/p&gt;\n\n			&lt;p&gt;2. Cơ quan quản l&amp;yacute; cạnh tranh thuộc Bộ Thương mại c&amp;oacute; tr&amp;aacute;ch nhiệm gi&amp;uacute;p Bộ trưởng Bộ Thương mại trong c&amp;aacute;c nội dung quản l&amp;yacute; cụ thể sau đ&amp;acirc;y:&lt;/p&gt;\n\n			&lt;p&gt;a) Hướng dẫn cho c&amp;aacute;c Sở Thương mại hoặc Sở Thương mại - Du lịch trong việc cấp Giấy đăng k&amp;yacute; tổ chức b&amp;aacute;n h&amp;agrave;ng đa cấp; kiểm tra việc quản l&amp;yacute; hoạt động b&amp;aacute;n h&amp;agrave;ng đa cấp của c&amp;aacute;c Sở Thương mại, Sở Thương mại - Du lịch;&lt;/p&gt;\n\n			&lt;p&gt;b) Trực tiếp kiểm tra, thanh tra hoạt động b&amp;aacute;n h&amp;agrave;ng đa cấp khi cần thiết. Xử l&amp;yacute; theo thẩm quyền c&amp;aacute;c h&amp;agrave;nh vi vi phạm quy định của ph&amp;aacute;p luật về hoạt động b&amp;aacute;n h&amp;agrave;ng đa cấp.&lt;/p&gt;\n\n			&lt;p&gt;c) Kiến nghị với Ch&amp;iacute;nh phủ ban h&amp;agrave;nh hoặc sửa đổi c&amp;aacute;c văn bản ph&amp;aacute;p luật c&amp;oacute; li&amp;ecirc;n quan đến việc đảm bảo trật tự trong tổ chức hoạt động b&amp;aacute;n h&amp;agrave;ng đa cấp, bảo vệ quyền lợi của người tham gia mạng lưới b&amp;aacute;n h&amp;agrave;ng đa cấp, người ti&amp;ecirc;u d&amp;ugrave;ng v&amp;agrave; giữ g&amp;igrave;n ổn định kinh tế - x&amp;atilde; hội.&lt;/p&gt;\n\n			&lt;p&gt;&lt;strong&gt;Điều&amp;nbsp;&lt;a name=&quot;Dieu_22&quot;&gt;&lt;/a&gt;22. Tr&amp;aacute;ch nhiệm của Uỷ ban nh&amp;acirc;n d&amp;acirc;n tỉnh, th&amp;agrave;nh phố trực thuộc Trung ương&lt;/strong&gt;&lt;/p&gt;\n\n			&lt;p&gt;1. Uỷ ban nh&amp;acirc;n d&amp;acirc;n tỉnh, th&amp;agrave;nh phố trực thuộc Trung ương c&amp;oacute; tr&amp;aacute;ch nhiệm quản l&amp;yacute; nh&amp;agrave; nước đối với phương thức b&amp;aacute;n h&amp;agrave;ng đa cấp theo thẩm quyền v&amp;agrave; theo sự chỉ đạo, hướng dẫn của Bộ Thương mại theo quy định của Nghị định n&amp;agrave;y v&amp;agrave; c&amp;aacute;c quy định kh&amp;aacute;c của ph&amp;aacute;p luật c&amp;oacute; li&amp;ecirc;n quan.&lt;/p&gt;\n\n			&lt;p&gt;2. Sở Thương mại hoặc Sở Thương mại - Du lịch gi&amp;uacute;p Uỷ ban nh&amp;acirc;n d&amp;acirc;n tỉnh, th&amp;agrave;nh phố trực thuộc Trung ương cấp Giấy đăng k&amp;yacute; tổ chức b&amp;aacute;n h&amp;agrave;ng đa cấp; thường xuy&amp;ecirc;n kiểm tra, gi&amp;aacute;m s&amp;aacute;t hoạt động b&amp;aacute;n h&amp;agrave;ng đa cấp tr&amp;ecirc;n địa b&amp;agrave;n v&amp;agrave; b&amp;aacute;o c&amp;aacute;o định kỳ với Cơ quan quản l&amp;yacute; cạnh tranh thuộc Bộ Thương mại về việc kiểm tra, gi&amp;aacute;m s&amp;aacute;t đ&amp;oacute;.&lt;/p&gt;\n\n			&lt;p&gt;&lt;strong&gt;Điều&amp;nbsp;&lt;a name=&quot;Dieu_23&quot;&gt;&lt;/a&gt;23. Xử l&amp;yacute; vi phạm đối với doanh nghiệp b&amp;aacute;n h&amp;agrave;ng đa cấp, người tham gia b&amp;aacute;n h&amp;agrave;ng đa cấp&lt;/strong&gt;&lt;/p&gt;\n\n			&lt;p&gt;1. Doanh nghiệp b&amp;aacute;n h&amp;agrave;ng đa cấp, người tham gia b&amp;aacute;n h&amp;agrave;ng đa cấp c&amp;oacute; h&amp;agrave;nh vi vi phạm sau đ&amp;acirc;y th&amp;igrave; t&amp;ugrave;y theo t&amp;iacute;nh chất, mức độ vi phạm m&amp;agrave; bị xử phạt vi phạm h&amp;agrave;nh ch&amp;iacute;nh theo quy định của ph&amp;aacute;p luật về xử l&amp;yacute; vi phạm h&amp;agrave;nh ch&amp;iacute;nh:&lt;/p&gt;\n\n			&lt;p&gt;a) Tổ chức b&amp;aacute;n h&amp;agrave;ng đa cấp khi chưa đủ điều kiện quy định;&lt;/p&gt;\n\n			&lt;p&gt;b) Vi phạm quy định về đối tượng được tham gia b&amp;aacute;n h&amp;agrave;ng đa cấp;&lt;/p&gt;\n\n			&lt;p&gt;c) Vi phạm quy định về h&amp;agrave;ng ho&amp;aacute; được ph&amp;eacute;p kinh doanh theo phương thức b&amp;aacute;n h&amp;agrave;ng đa cấp;&lt;/p&gt;\n\n			&lt;p&gt;d) Kh&amp;ocirc;ng th&amp;ocirc;ng b&amp;aacute;o đầy đủ th&amp;ocirc;ng tin theo quy định khi bảo trợ người tham gia mới v&amp;agrave;o mạng lưới b&amp;aacute;n h&amp;agrave;ng;&lt;/p&gt;\n\n			&lt;p&gt;đ) Thực hiện những h&amp;agrave;nh vi m&amp;agrave; doanh nghiệp b&amp;aacute;n h&amp;agrave;ng đa cấp v&amp;agrave; người tham gia b&amp;aacute;n h&amp;agrave;ng đa cấp kh&amp;ocirc;ng được ph&amp;eacute;p thực hiện;&lt;/p&gt;\n\n			&lt;p&gt;e) Doanh nghiệp b&amp;aacute;n h&amp;agrave;ng đa cấp kh&amp;ocirc;ng k&amp;yacute; hợp đồng bằng văn bản với người tham gia;&lt;/p&gt;\n\n			&lt;p&gt;g) Vi phạm c&amp;aacute;c quy định về chấm dứt hợp đồng tham gia b&amp;aacute;n h&amp;agrave;ng đa cấp;&lt;/p&gt;\n\n			&lt;p&gt;h) Kh&amp;ocirc;ng chấp h&amp;agrave;nh chế độ b&amp;aacute;o c&amp;aacute;o theo quy định tại Điều 20 của Nghị định n&amp;agrave;y;&lt;/p&gt;\n\n			&lt;p&gt;i) Thay đổi nội dung của Chương tr&amp;igrave;nh b&amp;aacute;n h&amp;agrave;ng m&amp;agrave; kh&amp;ocirc;ng l&amp;agrave;m thủ tục đề nghị cấp bổ sung Giấy đăng k&amp;yacute; tổ chức b&amp;aacute;n h&amp;agrave;ng đa cấp;&lt;/p&gt;\n\n			&lt;p&gt;k) Doanh nghiệp b&amp;aacute;n h&amp;agrave;ng đa cấp vi phạm quy định về số tiền k&amp;yacute; quỹ, chi trả từ tiền k&amp;yacute; quỹ trong qu&amp;aacute; tr&amp;igrave;nh hoạt động;&lt;/p&gt;\n\n			&lt;p&gt;l) Kh&amp;ocirc;ng nộp thuế theo quy định của ph&amp;aacute;p luật;&lt;/p&gt;\n\n			&lt;p&gt;m) Kh&amp;ocirc;ng chấp h&amp;agrave;nh c&amp;aacute;c y&amp;ecirc;u cầu của cơ quan nh&amp;agrave; nước c&amp;oacute; thẩm quyền khi tiến h&amp;agrave;nh kiểm tra, thanh tra;&lt;/p&gt;\n\n			&lt;p&gt;n) Vi phạm c&amp;aacute;c quy định kh&amp;aacute;c của Nghị định n&amp;agrave;y.&lt;/p&gt;\n\n			&lt;p&gt;2. Trường hợp doanh nghiệp hoặc người tham gia b&amp;aacute;n h&amp;agrave;ng đa cấp vi phạm g&amp;acirc;y thiệt hại đến lợi &amp;iacute;ch vật chất của tổ chức, c&amp;aacute; nh&amp;acirc;n li&amp;ecirc;n quan th&amp;igrave; phải bồi thường thiệt hại theo quy định của ph&amp;aacute;p luật.&lt;/p&gt;\n\n			&lt;p&gt;&lt;strong&gt;Điều&amp;nbsp;&lt;a name=&quot;Dieu_24&quot;&gt;&lt;/a&gt;24. Thẩm quyền, thủ tục xử l&amp;yacute; vi phạm h&amp;agrave;nh ch&amp;iacute;nh&lt;/strong&gt;&lt;/p&gt;\n\n			&lt;p&gt;1. Thẩm quyền xử l&amp;yacute; vi phạm h&amp;agrave;nh ch&amp;iacute;nh đối với c&amp;aacute;c h&amp;agrave;nh vi quy định tại Điều 23 của Nghị định n&amp;agrave;y được thực hiện theo quy định của Luật Cạnh tranh v&amp;agrave; Ph&amp;aacute;p lệnh về xử l&amp;yacute; vi phạm h&amp;agrave;nh ch&amp;iacute;nh.&lt;/p&gt;\n\n			&lt;p&gt;2. Thủ tục xử l&amp;yacute; vi phạm h&amp;agrave;nh ch&amp;iacute;nh đối với c&amp;aacute;c h&amp;agrave;nh vi quy định tại Điều 23 của Nghị định n&amp;agrave;y được thực hiện theo quy định của ph&amp;aacute;p luật về xử l&amp;yacute; vi phạm h&amp;agrave;nh ch&amp;iacute;nh.&lt;/p&gt;\n\n			&lt;p&gt;&lt;strong&gt;Chương&amp;nbsp;&lt;a name=&quot;Chuong_IV&quot;&gt;&lt;/a&gt;IV&lt;/strong&gt;&lt;/p&gt;\n\n			&lt;p&gt;&lt;strong&gt;ĐIỀU KHOẢN THI H&amp;Agrave;NH&lt;/strong&gt;&lt;/p&gt;\n\n			&lt;p&gt;&lt;strong&gt;Điều&amp;nbsp;&lt;a name=&quot;Dieu_25&quot;&gt;&lt;/a&gt;25. Hiệu lực thi h&amp;agrave;nh&lt;/strong&gt;&lt;/p&gt;\n\n			&lt;p&gt;1. Nghị định n&amp;agrave;y c&amp;oacute; hiệu lực thi h&amp;agrave;nh sau 15 ng&amp;agrave;y, kể từ ng&amp;agrave;y đăng C&amp;ocirc;ng b&amp;aacute;o. Những doanh nghiệp b&amp;aacute;n h&amp;agrave;ng đa cấp đ&amp;atilde; đăng k&amp;yacute; kinh doanh trước thời điểm Nghị định n&amp;agrave;y c&amp;oacute; hiệu lực c&amp;oacute; tr&amp;aacute;ch nhiệm l&amp;agrave;m thủ tục đề nghị cấp Giấy đăng k&amp;yacute; tổ chức b&amp;aacute;n h&amp;agrave;ng đa cấp tại Sở Thương mại hoặc Sở Thương mại - Du lịch cấp tỉnh nơi doanh nghiệp đăng k&amp;yacute; kinh doanh trong thời hạn 3 th&amp;aacute;ng kể từ ng&amp;agrave;y Nghị định n&amp;agrave;y c&amp;oacute; hiệu lực.&lt;/p&gt;\n\n			&lt;p&gt;2. C&amp;aacute;c Bộ trưởng, Thủ trưởng cơ quan ngang Bộ, Thủ trưởng c&amp;aacute;c cơ quan thuộc Ch&amp;iacute;nh phủ, Chủ tịch Uỷ ban nh&amp;acirc;n d&amp;acirc;n tỉnh, th&amp;agrave;nh phố trực thuộc Trung ương chịu tr&amp;aacute;ch nhiệm thi h&amp;agrave;nh Nghị định n&amp;agrave;y./.&lt;/p&gt;\n			&lt;/td&gt;\n		&lt;/tr&gt;\n		&lt;tr&gt;\n			&lt;td colspan=&quot;3&quot;&gt;&amp;nbsp;&lt;/td&gt;\n		&lt;/tr&gt;\n	&lt;/tbody&gt;\n&lt;/table&gt;\n', '', '', '', 'nghi-dinh-ve-quan-ly-hoat-dong-ban-hang-da-cap', 12, 0, 1, 0, '2015-10-12 03:46:19', '2015-10-12 03:46:19', 1, 1, 1);
INSERT INTO `cms_news` (`id`, `title`, `thumbnail`, `description`, `content`, `meta_title`, `meta_keyword`, `meta_description`, `alias`, `category_id`, `is_featured`, `publish`, `order`, `created_date`, `updated_date`, `userid_created`, `fb_share_like`, `fb_comment`) VALUES
(23, 'Ra mắt thêm sản phẩm Bổ Xương Khớp', '/public/images/sanpham/PC%20VienBoKhop.jpg', '&lt;p&gt;Ng&amp;agrave;y 01/10/2015 Sen Việt Group ch&amp;iacute;nh thức ra mắt sản phẩm Bổ Xương Khớp.&lt;/p&gt;\n', '&lt;p&gt;&amp;nbsp;&lt;/p&gt;\n\n&lt;p&gt;&lt;span style=&quot;font-size:14px&quot;&gt;&lt;span style=&quot;font-family:times new roman,times,serif&quot;&gt;&lt;strong&gt;BẢN TH&amp;Ocirc;NG TIN CHI TIẾT VỀ SẢN PHẨM&lt;/strong&gt;&lt;br /&gt;\n&lt;strong&gt;_________&lt;/strong&gt;&lt;br /&gt;\n&lt;strong&gt;Thực phẩm chức năng Số: 001/14- TCTP&lt;/strong&gt; C&amp;Ocirc;NG TY TNHH LINKLIFE CHI NH&amp;Aacute;NH H&amp;Agrave; NỘI&amp;nbsp;VI&amp;Ecirc;N KHỚP (Osteoarthritis Supplements)&lt;br /&gt;\n&lt;strong&gt;1. Y&amp;Ecirc;U CẦU KỸ THUẬT&lt;/strong&gt;&lt;/span&gt;&lt;/span&gt;&lt;br /&gt;\n&lt;span style=&quot;font-size:14px&quot;&gt;&lt;span style=&quot;font-family:times new roman,times,serif&quot;&gt;&lt;strong&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp;1.1. C&amp;aacute;c chỉ ti&amp;ecirc;u cảm quan:&lt;/strong&gt;&lt;br /&gt;\n&lt;strong&gt;Stt T&amp;ecirc;n chỉ ti&amp;ecirc;u Y&amp;ecirc;u cầu&lt;/strong&gt; 1 Trạng th&amp;aacute;i vi&amp;ecirc;n nang cứng 2 M&amp;agrave;u sắc đ 3 M&amp;ugrave;i, vị m&amp;ugrave;i thơm hương iệu, dược iệu, kh&amp;ocirc;ng &amp;ocirc;i mốc. 4 C&amp;aacute;c đặc t&amp;iacute;nh kh&amp;aacute;c&lt;br /&gt;\n&lt;strong&gt;1.2. Chỉ ti&amp;ecirc;u chất lƣợng chủ yếu:&lt;/strong&gt;&lt;br /&gt;\n&lt;strong&gt;Stt T&amp;ecirc;n chỉ ti&amp;ecirc;u Đơn vị t&amp;iacute;nh Mức c&amp;ocirc;ng bố Mức đ&amp;aacute;p ứng/&lt;/strong&gt;&lt;br /&gt;\n&lt;strong&gt;khẩu phần ăn&lt;/strong&gt; 1 Glucosamin mg/ vi&amp;ecirc;n 320-480 2 Chondroitin mg/vi&amp;ecirc;n 8-12 3 MSM mg/ vi&amp;ecirc;n 40-60 4 Ph ng phong Định t&amp;iacute;nh Dương t&amp;iacute;nh 5 Độ ẩm % &amp;lt; 9 6 Độ tan r&amp;atilde; Ph&amp;uacute;t &amp;le; 30 7 H&amp;agrave;m ượng tro kh&amp;ocirc;ng tan trong HCl % &amp;lt; 5&lt;br /&gt;\n&lt;strong&gt;1.3. C&amp;aacute;c chỉ ti&amp;ecirc;u vi sinh vật:&lt;/strong&gt;&lt;br /&gt;\n&lt;strong&gt;Stt T&amp;ecirc;n chỉ ti&amp;ecirc;u Đơn vị t&amp;iacute;nh Mức tối đa&lt;/strong&gt; 1 Tổng số vi khuẩn hiếu kh&amp;iacute; CFU/g 104 2 Coliforms CFU/g 10 3 Escherichia Coli CFU/g 3 4 Salmonella CFU/25g 0 5 Staphylococcus Aureus CFU/g 10 6 Bacillus Cereus CFU/g 10 7 TSBTNM-NM CFU/g 102&lt;br /&gt;\n&lt;strong&gt;1.4. H&amp;agrave;m lƣợng kim loại nặng:&lt;/strong&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\n\n&lt;p&gt;&lt;span style=&quot;font-size:14px&quot;&gt;&lt;span style=&quot;font-family:times new roman,times,serif&quot;&gt;&lt;strong&gt;Stt T&amp;ecirc;n chỉ ti&amp;ecirc;u Đơn vị t&amp;iacute;nh Mức tối đa&lt;/strong&gt; 1 Pb ppm 3 2 Hg ppm 0,1 3 Cd ppm 1&lt;br /&gt;\n&lt;strong&gt;1.5. H&amp;agrave;m lƣợng h&amp;oacute;a chất kh&amp;ocirc;ng mong muốn&lt;/strong&gt;&lt;br /&gt;\n&lt;strong&gt;Stt T&amp;ecirc;n chỉ ti&amp;ecirc;u Đơn vị t&amp;iacute;nh Mức tối đa&lt;/strong&gt; 1 H&amp;agrave;m ượng Af atoxin 1 ppb &amp;le; 5 2 H&amp;agrave;m ượng Af atoxin 1, 2,G1,G2 ppb &amp;le; 15&lt;br /&gt;\nDư ượng thuốc bảo vệ thực vật ph&amp;ugrave; hợp với quy định tại quyết định 46/2007/QD - BYT&lt;br /&gt;\nNg&amp;agrave;y 19/12/2007 của ộ y Tế&lt;br /&gt;\n&lt;strong&gt;1.6. C&amp;aacute;c chỉ ti&amp;ecirc;u kh&amp;aacute;c:&lt;/strong&gt;&lt;br /&gt;\n&lt;strong&gt;2. TH&amp;Agrave;NH PHẦN CẤU TẠO&lt;/strong&gt; Glucosamin 400 mg Chondroitin 10 mg MSM 50 mg&lt;br /&gt;\nĐộc hoạt 100 mg&lt;br /&gt;\nNgưu tất 100 mg&lt;br /&gt;\nPh ng phong 200 mg Xuy&amp;ecirc;n khung 50 mg&lt;br /&gt;\nCốt to&amp;aacute;i bổ 50 mg&lt;br /&gt;\nTh&amp;agrave;nh phần kh&amp;aacute;c magnesium stearate, ta c&amp;hellip; : vừa đủ 1 vi&amp;ecirc;n&lt;br /&gt;\n&lt;strong&gt;3. THỜI HẠN SỬ DỤNG:&lt;/strong&gt;&lt;br /&gt;\n03 năm kể từ ng&amp;agrave;y sản xuất. Ng&amp;agrave;y sản xuất v&amp;agrave; hạn sử dụng ghi ở tr&amp;ecirc;n nh&amp;atilde;n sản phẩm&lt;br /&gt;\n&lt;strong&gt;4. HƢỚNG DẪN SỬ DỤNG V&amp;Agrave; BẢO QUẢN:&lt;/strong&gt;&lt;br /&gt;\n&lt;strong&gt;C&amp;ocirc;ng dụng:&lt;/strong&gt;&lt;br /&gt;\nGi&amp;uacute;p phục hồi, t&amp;aacute;i tạo m&amp;ocirc; sụn khớp, tăng tiết dịch khớp, đảm bảo sự vận động inh hoạt của&lt;br /&gt;\nkhớp, giảm đau, b&amp;ocirc;i trơn khớp. Hỗ trợ iệu ph&amp;aacute;p điều trị tho&amp;aacute;i ho&amp;aacute; khớp, đau m i khớp.&lt;br /&gt;\n&lt;strong&gt;Đối tƣợng:&lt;/strong&gt; - Đối tượng d&amp;ugrave;ng: Người tho&amp;aacute;i ho&amp;aacute; xương khớp, đau m i khớp, đau v&amp;agrave; nhức xương&lt;br /&gt;\nkhớp. - Ch&amp;uacute; &amp;yacute;:&lt;br /&gt;\nSản phẩm n&amp;agrave;y kh&amp;ocirc;ng g&amp;acirc;y rối oạn ruột, dạ d&amp;agrave;y n&amp;ecirc;n c&amp;oacute; thể d&amp;ugrave;ng &amp;acirc;u d&amp;agrave;i&lt;br /&gt;\nSản phẩm c&amp;oacute; t&amp;aacute;c động &amp;ecirc;n nguy&amp;ecirc;n nh&amp;acirc;n g&amp;acirc;y tho&amp;aacute;i ho&amp;aacute; khớp. Do đ&amp;oacute;, t&amp;aacute;c dụng c&amp;oacute; thể&lt;br /&gt;\nbiểu hiện chậm sau khoảng 1 tuần sử dụng.&lt;br /&gt;\nThận trọng cho phụ nữ c&amp;oacute; thai v&amp;agrave; cho con b&amp;uacute;.&lt;br /&gt;\nSản phẩm n&amp;agrave;y kh&amp;ocirc;ng phải &amp;agrave; thuốc v&amp;agrave; kh&amp;ocirc;ng c&amp;oacute; t&amp;aacute;c dụng thay thế thuốc chữa bệnh&lt;br /&gt;\n&lt;strong&gt;Hƣớng dẫn sử dụng v&amp;agrave; bảo quản:&lt;/strong&gt;&lt;br /&gt;\n* C&amp;aacute;ch sử dụng:&lt;br /&gt;\nNgười ớn: uống 2 vi&amp;ecirc;n/ ần x 2 ần/ng&amp;agrave;y.&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\n\n&lt;p&gt;&lt;span style=&quot;font-size:14px&quot;&gt;&lt;span style=&quot;font-family:times new roman,times,serif&quot;&gt;Để c&amp;oacute; t&amp;aacute;c dụng hiệu quả n&amp;ecirc;n d&amp;ugrave;ng trong 6 - 8 tuần, mỗi năm c&amp;oacute; thể uống ặp ại 2 - 3&lt;br /&gt;\nần.&lt;br /&gt;\n* ảo quản: Nơi kh&amp;ocirc; r&amp;aacute;o tho&amp;aacute;ng m&amp;aacute;t tr&amp;aacute;nh &amp;aacute;nh s&amp;aacute;ng.&lt;br /&gt;\n&lt;strong&gt;5. CHẤT LIỆU BAO B&amp;Igrave; V&amp;Agrave; QUY C&amp;Aacute;CH Đ&amp;Oacute;NG G&amp;Oacute;I&lt;/strong&gt;&lt;br /&gt;\n* Chất iệu bao b&amp;igrave;: Lọ nhựa, nắp nhựa, m&amp;agrave;ng nh&amp;ocirc;m, m&amp;agrave;u trắng ph&amp;ugrave; hợp với ti&amp;ecirc;u&lt;br /&gt;\nchuẩn bao b&amp;igrave; d&amp;ugrave;ng cho thực phẩm.&lt;br /&gt;\n* Qui c&amp;aacute;ch đ&amp;oacute;ng g&amp;oacute;i:&lt;br /&gt;\nLọ: 30, 50, 60, 90, 100, 120, 180 vi&amp;ecirc;n&lt;br /&gt;\nVỉ 10 vi&amp;ecirc;n; hộp 2 vỉ, 3 vỉ, 5 vỉ, 6 vỉ, 9 vỉ, 10 vỉ&lt;br /&gt;\n&lt;strong&gt;6. QUY TR&amp;Igrave;NH SẢN XUẤT&lt;/strong&gt;&lt;br /&gt;\n&lt;strong&gt;7. C&amp;Aacute;C BIỆN PH&amp;Aacute;P PH&amp;Acirc;N BIỆT THẬT, GIẢ NẾU C&amp;Oacute; &lt;/strong&gt;&lt;br /&gt;\nĐăng k&amp;yacute; bảo hộ nh&amp;atilde;n hiệu h&amp;agrave;ng ho&amp;aacute;&lt;br /&gt;\n&lt;strong&gt;8. NỘI DUNG GHI NH&amp;Atilde;N&lt;/strong&gt;&lt;br /&gt;\nXem phụ ục đ&amp;iacute;nh k&amp;egrave;m&lt;br /&gt;\n&lt;strong&gt;9. XUẤT XỨ V&amp;Agrave; THƢƠNG NH&amp;Acirc;N CHỊU TR&amp;Aacute;CH NHIỆM VỀ CHẤT LƢỢNG H&amp;Agrave;NG&lt;/strong&gt;&lt;br /&gt;\n&lt;strong&gt;H&amp;Oacute;A&lt;/strong&gt;&lt;br /&gt;\n&lt;strong&gt;Thƣơng nh&amp;acirc;n nhập khẩu v&amp;agrave; ph&amp;acirc;n phối:&lt;/strong&gt; - T&amp;ecirc;n thương nh&amp;acirc;n: C&amp;Ocirc;NG TY TNHH LINK LIFE CHI NH&amp;Aacute;NH H&amp;Agrave; NỘI - Địa chỉ: Số 148 ng&amp;otilde; 521, đường Trương Định, Phường T&amp;acirc;n Mai, Quận Ho&amp;agrave;ng Mai, Th&amp;agrave;nh&lt;br /&gt;\nphố H&amp;agrave; Nội, Việt Nam - Điện thoại: 0436611554&lt;br /&gt;\n&lt;strong&gt;H&amp;agrave; Nội, ng&amp;agrave;y 14 th&amp;aacute;ng 11 năm 2014&lt;/strong&gt;&lt;br /&gt;\n&lt;strong&gt;ĐẠI DIỆN T CHỨC, C&amp;Aacute; NH&amp;Acirc;N&lt;/strong&gt;&lt;br /&gt;\n&lt;strong&gt;Phạm Trung Ki&amp;ecirc;n&lt;/strong&gt;&lt;/span&gt;&lt;/span&gt;&lt;br /&gt;\n&lt;br /&gt;\n&amp;nbsp;&lt;/p&gt;\n', '', '', '', 'ra-mat-them-san-pham-bo-xuong-khop', 7, 0, 1, 0, '2015-10-12 03:56:45', '2015-10-15 04:33:02', 1, 1, 1),
(24, 'Ra mắt ban lãnh đạo Sen Việt Group', '/public/images/gioi%20thieu%20-%20ban%20lanh%20dao.jpg', '&lt;p&gt;Ng&amp;agrave;y ,..../............/2015&lt;/p&gt;\n\n&lt;p&gt;Sen Việt Group họp mặt v&amp;agrave; ra mắt ban l&amp;atilde;nh đạo&lt;/p&gt;\n', '&lt;p&gt;Chi tiết th&amp;ocirc;ng tin ...........&lt;/p&gt;\n', '', '', '', 'ra-mat-ban-lanh-dao-sen-viet-group', 8, 0, 1, 0, '2015-10-12 03:59:32', '2015-10-12 03:59:32', 1, 0, 0),
(26, '6 triết lý kinh doanh đưa đến thành công của CEO Charles &amp; Keith', '/public/images/triet-ly.jpg', '&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;strong&gt;Ở tuổi 39, Charles Wong, CEO của thương hiệu Charles &amp;amp; Keith đang nắm trong tay c&amp;ocirc;ng ty trị gi&amp;aacute; h&amp;agrave;ng triệu USD với hơn 300 cửa h&amp;agrave;ng tr&amp;ecirc;n to&amp;agrave;n thế giới.&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;\n\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\n\n&lt;p&gt;Charles &amp;amp; Keith kh&amp;ocirc;ng chỉ l&amp;agrave; thương hiệu y&amp;ecirc;u th&amp;iacute;ch của nhiều phụ nữ Singapore v&amp;agrave; dường như cũng kh&amp;ocirc;ng c&amp;ograve;n xa lạ với những t&amp;iacute;n đồ thời trang gi&amp;agrave;y d&amp;eacute;p, t&amp;uacute;i x&amp;aacute;ch trong khu vực Đ&amp;ocirc;ng Nam &amp;Aacute;, trong đ&amp;oacute; c&amp;oacute; Việt Nam.&lt;/p&gt;\n\n&lt;p&gt;Charles Wong bắt đầu c&amp;ocirc;ng việc kinh doanh từ năm 1996 với một cửa h&amp;agrave;ng nhỏ tại Trung t&amp;acirc;m mua sắm Amara. Kể từ đ&amp;oacute;, Charles &amp;amp; Keith đ&amp;atilde; ph&amp;aacute;t triển lớn mạnh bất chấp sự cạnh tranh của c&amp;aacute;c thương hiệu lớn v&amp;agrave; g&amp;acirc;y dựng được hệ thống hơn 300 cửa h&amp;agrave;ng tr&amp;ecirc;n to&amp;agrave;n thế giới.&lt;/p&gt;\n\n&lt;p&gt;Kh&amp;ocirc;ng chỉ vậy, th&amp;agrave;nh c&amp;ocirc;ng của Charles &amp;amp; Keith c&amp;ograve;n khiến &amp;ldquo;g&amp;atilde; khổng lồ&amp;rdquo; trong ng&amp;agrave;nh thời trang cao cấp của Ph&amp;aacute;p Louis Vuitton Moet Hennessy (LVMH) phải để mắt tới. Hiện tại, LVMH đang nắm giữ 20% cổ phần của Charles &amp;amp; Keith, theo Vulcanpost.&lt;/p&gt;\n', '&lt;p&gt;Charles &amp;amp; Keith kh&amp;ocirc;ng chỉ l&amp;agrave; thương hiệu y&amp;ecirc;u th&amp;iacute;ch của nhiều phụ nữ Singapore v&amp;agrave; dường như cũng kh&amp;ocirc;ng c&amp;ograve;n xa lạ với những t&amp;iacute;n đồ thời trang gi&amp;agrave;y d&amp;eacute;p, t&amp;uacute;i x&amp;aacute;ch trong khu vực Đ&amp;ocirc;ng Nam &amp;Aacute;, trong đ&amp;oacute; c&amp;oacute; Việt Nam.&lt;/p&gt;\n\n&lt;p&gt;Charles Wong bắt đầu c&amp;ocirc;ng việc kinh doanh từ năm 1996 với một cửa h&amp;agrave;ng nhỏ tại Trung t&amp;acirc;m mua sắm Amara. Kể từ đ&amp;oacute;, Charles &amp;amp; Keith đ&amp;atilde; ph&amp;aacute;t triển lớn mạnh bất chấp sự cạnh tranh của c&amp;aacute;c thương hiệu lớn v&amp;agrave; g&amp;acirc;y dựng được hệ thống hơn 300 cửa h&amp;agrave;ng tr&amp;ecirc;n to&amp;agrave;n thế giới.&lt;/p&gt;\n\n&lt;p&gt;Kh&amp;ocirc;ng chỉ vậy, th&amp;agrave;nh c&amp;ocirc;ng của Charles &amp;amp; Keith c&amp;ograve;n khiến &amp;ldquo;g&amp;atilde; khổng lồ&amp;rdquo; trong ng&amp;agrave;nh thời trang cao cấp của Ph&amp;aacute;p Louis Vuitton Moet Hennessy (LVMH) phải để mắt tới. Hiện tại, LVMH đang nắm giữ 20% cổ phần của Charles &amp;amp; Keith, theo Vulcanpost.&lt;/p&gt;\n\n&lt;p&gt;Chia sẻ về th&amp;agrave;nh c&amp;ocirc;ng trong kinh doanh, doanh nh&amp;acirc;n trẻ Charles Wong g&amp;oacute;i gọn trong 6 b&amp;iacute; quyết dưới đ&amp;acirc;y:&lt;/p&gt;\n\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;strong&gt;1. Kh&amp;ocirc;ng g&amp;igrave; nhạy b&amp;eacute;n bằng bản năng kinh doanh tự nhi&amp;ecirc;n&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;\n\n&lt;p&gt;Trong suy nghĩ của người Singapore n&amp;oacute;i chung, th&amp;agrave;nh c&amp;ocirc;ng thường gắn liền với một nền tảng gi&amp;aacute;o dục tuyệt vời, nhưng Charles Wong lại c&amp;oacute; một khởi điểm kh&amp;oacute; khăn. Kh&amp;ocirc;ng giống như người em trai của &amp;ocirc;ng -Keith Wong - người duy nhất trong gia đ&amp;igrave;nh được học đại học, Charles c&amp;ograve;n chưa lấy được chứng chỉ &amp;ldquo;O&amp;rdquo; (kết quả của kỳ thi &amp;ldquo;O&amp;rdquo; với c&amp;aacute;c m&amp;ocirc;n thi To&amp;aacute;n, Lịch sử v&amp;agrave; Kinh th&amp;aacute;nh l&amp;agrave; căn cứ để x&amp;eacute;t tuyển v&amp;agrave;o c&amp;aacute;c chương tr&amp;igrave;nh gi&amp;aacute;o dục cao hơn sau khi tốt nghiệp trung học cơ sở).&lt;/p&gt;\n\n&lt;p&gt;Mặc d&amp;ugrave; vậy, Charles đ&amp;atilde; bắt đầu g&amp;acirc;y dựng Charles &amp;amp; Keith ở tuổi 22. Người em trai Keith tham gia v&amp;agrave;o c&amp;ocirc;ng việc kinh doanh c&amp;ugrave;ng anh trai muộn hơn hai năm, sau khi ho&amp;agrave;n th&amp;agrave;nh nghĩa vụ qu&amp;acirc;n sự.&lt;/p&gt;\n\n&lt;p&gt;D&amp;ugrave; kh&amp;ocirc;ng được học h&amp;agrave;nh b&amp;agrave;i bản nhưng Charles v&amp;agrave; Keith đ&amp;atilde; dần x&amp;acirc;y dựng c&amp;ocirc;ng ty từ một cửa h&amp;agrave;ng nhỏ th&amp;agrave;nh doanh nghiệp h&amp;agrave;ng triệu USD - v&amp;agrave; họ c&amp;ograve;n c&amp;oacute; kế hoạch mở rộng hơn nữa. Th&amp;agrave;nh c&amp;ocirc;ng của Charles ch&amp;iacute;nh l&amp;agrave; minh chứng tuyệt vời cho thấy tinh thần doanh nh&amp;acirc;n v&amp;agrave; bản năng kinh doanh nhạy b&amp;eacute;n sẽ gi&amp;uacute;p nhiều người tiến xa trong cuộc sống, miễn l&amp;agrave; họ nỗ lực.&lt;/p&gt;\n\n&lt;p&gt;Charles Wong từng tiết lộ trong một cuộc phỏng vấn về b&amp;iacute; quyết th&amp;agrave;nh c&amp;ocirc;ng của &amp;ocirc;ng ch&amp;iacute;nh l&amp;agrave; &amp;ldquo;nghĩ lớn v&amp;agrave; kh&amp;ocirc;ng sợ thất bại ngay cả khi bạn kh&amp;ocirc;ng th&amp;agrave;nh c&amp;ocirc;ng từ lần nỗ lực đầu ti&amp;ecirc;n&amp;quot;.&lt;/p&gt;\n\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;strong&gt;2. Kinh nghiệm kinh doanh của cha mẹ thực sự đ&amp;aacute;ng để học hỏi&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;\n\n&lt;p&gt;Trước khi g&amp;acirc;y dựng thương hiệu Charles &amp;amp; Keith, Charles v&amp;agrave; Keith Wong từng gi&amp;uacute;p mẹ kinh doanh cửa h&amp;agrave;ng gi&amp;agrave;y ở Ang Mo Kio. Trong thời gian n&amp;agrave;y Charles đ&amp;atilde; học hỏi được những kinh nghiệm để điều h&amp;agrave;nh một doanh nghiệp gi&amp;agrave;y th&amp;agrave;nh c&amp;ocirc;ng v&amp;agrave; nu&amp;ocirc;i dưỡng kh&amp;aacute;t vọng mở doanh nghiệp của ri&amp;ecirc;ng m&amp;igrave;nh.&lt;/p&gt;\n\n&lt;p&gt;Trong khi mẹ &amp;ocirc;ng muốn đ&amp;oacute;ng cửa cửa h&amp;agrave;ng gi&amp;agrave;y đang kinh doanh ế ẩm, th&amp;igrave; Charles lại quyết t&amp;acirc;m vực dậy. Sau khi tiếp quản cửa h&amp;agrave;ng gi&amp;agrave;y, &amp;ocirc;ng cố gắng xoay chuyển t&amp;igrave;nh thế, t&amp;iacute;ch lũy từng đồng tiền kiếm được đồng thời kh&amp;ocirc;ng ngại lăn xả v&amp;agrave;o những thử th&amp;aacute;ch bằng sức trẻ.&lt;/p&gt;\n\n&lt;p&gt;Những kinh nghiệm n&amp;agrave;y cuối c&amp;ugrave;ng đ&amp;atilde; trở th&amp;agrave;nh những kỹ năng cần thiết để &amp;ocirc;ng vận dụng v&amp;agrave;o c&amp;ocirc;ng ty sau n&amp;agrave;y.&lt;/p&gt;\n\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;strong&gt;3. T&amp;igrave;m ra v&amp;agrave; ph&amp;aacute;t huy tốt nhất lợi thế của bản th&amp;acirc;n&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;\n\n&lt;p&gt;Điều h&amp;agrave;nh một doanh nghiệp kinh doanh gi&amp;agrave;y cũng giống như một giọt nước giữa đại dương bao la - kh&amp;ocirc;ng c&amp;oacute; nhiều c&amp;aacute;ch để trở n&amp;ecirc;n nổi bật. Từng đau đầu trước sự mờ nhạt của doanh nghiệp m&amp;igrave;nh, Charles nhận ra rằng m&amp;igrave;nh phải t&amp;igrave;m c&amp;aacute;ch để trở n&amp;ecirc;n kh&amp;aacute;c biệt so với đ&amp;ocirc;ng đảo c&amp;aacute;c đối thủ cạnh tranh.&lt;/p&gt;\n\n&lt;p&gt;Sau khi n&amp;oacute;i chuyện với kh&amp;aacute;ch h&amp;agrave;ng, &amp;ocirc;ng ph&amp;aacute;t hiện ra rằng b&amp;aacute;n gi&amp;agrave;y mua lại từ c&amp;aacute;c nh&amp;agrave; cung cấp b&amp;aacute;n bu&amp;ocirc;n kh&amp;ocirc;ng thể đ&amp;aacute;p ứng đủ c&amp;aacute;c nhu cầu đa dạng của kh&amp;aacute;ch h&amp;agrave;ng. Muốn doanh nghiệp tồn tại, mọi thứ cần phải thay đổi.&lt;/p&gt;\n\n&lt;p&gt;Điều n&amp;agrave;y khiến anh em Charles c&amp;agrave;ng th&amp;ecirc;m quyết t&amp;acirc;m thiết kế c&amp;aacute;c mẫu gi&amp;agrave;y của ri&amp;ecirc;ng m&amp;igrave;nh v&amp;agrave;o cuối năm 1997. Trong v&amp;ograve;ng ba năm, tất cả c&amp;aacute;c mẫu gi&amp;agrave;y Charles &amp;amp; Keith đều do h&amp;atilde;ng tự thiết kế ho&amp;agrave;n to&amp;agrave;n, đ&amp;oacute; l&amp;agrave; lợi điểm b&amp;aacute;n h&amp;agrave;ng lớn khiến Charles &amp;amp; Keith kh&amp;aacute;c biệt hẳn so với c&amp;aacute;c đối thủ cạnh tranh. Đến nay, đội ngũ thiết kế gồm 70 người của Charles &amp;amp; Keith c&amp;oacute; khả năng tạo ra hơn một ng&amp;agrave;n mẫu gi&amp;agrave;y v&amp;agrave; phụ kiện mới mỗi năm, đồng thời tạo một mẫu gi&amp;agrave;y hoặc phụ kiện mỗi ng&amp;agrave;y cho thương hiệu Pedro, theo Forbes.&amp;quot;Rất nhiều lần kh&amp;aacute;ch h&amp;agrave;ng gửi cho t&amp;ocirc;i th&amp;ocirc;ng tin phản hồi về thiết kế họ muốn v&amp;agrave; t&amp;ocirc;i gửi lại c&amp;aacute;c &amp;yacute; kiến n&amp;agrave;y tới c&amp;aacute;c nh&amp;agrave; cung cấp. Nhưng họ kh&amp;ocirc;ng thể cung cấp những mẫu thiết kế m&amp;agrave; kh&amp;aacute;ch h&amp;agrave;ng mong muốn. Ngo&amp;agrave;i ra, mức gi&amp;aacute; m&amp;agrave; họ đưa ra kh&amp;ocirc;ng đủ sức cạnh tranh&amp;quot;, &amp;ocirc;ng chia sẻ tr&amp;ecirc;n tạp ch&amp;iacute; Forbes.&lt;/p&gt;\n\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;strong&gt;4. Kh&amp;oacute; khăn của người kh&amp;aacute;c c&amp;oacute; thể l&amp;agrave; cơ hội của bạn&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;\n\n&lt;p&gt;Khi nền kinh tế Singapore phải đối mặt với cuộc khủng hoảng kinh tế ch&amp;acirc;u &amp;Aacute; năm 1997, Charles lại coi đ&amp;oacute; l&amp;agrave; cơ hội để th&amp;uacute;c đẩy việc kinh doanh theo một hướng đi mới.&lt;/p&gt;\n\n&lt;p&gt;&amp;quot;Ch&amp;uacute;ng t&amp;ocirc;i nh&amp;igrave;n thấy cơ hội l&amp;agrave;m việc trực tiếp với một số nh&amp;agrave; m&amp;aacute;y để gi&amp;agrave;nh th&amp;ecirc;m quyền kiểm so&amp;aacute;t trong qu&amp;aacute; tr&amp;igrave;nh thiết kế cũng như sản xuất. Ch&amp;uacute;ng t&amp;ocirc;i tạo ra c&amp;aacute;c mẫu thiết kế dựa tr&amp;ecirc;n phản hồi của kh&amp;aacute;ch h&amp;agrave;ng v&amp;agrave; ch&amp;uacute;ng t&amp;ocirc;i nhận ra rằng nếu hiểu r&amp;otilde; những g&amp;igrave; kh&amp;aacute;ch h&amp;agrave;ng mong muốn, mỗi m&amp;ugrave;a ch&amp;uacute;ng t&amp;ocirc;i đều c&amp;oacute; thể tạo ra những sản phẩm th&amp;uacute; vị&amp;quot;, Charles Wong chia sẻ với LuxuryInsider.&lt;/p&gt;\n\n&lt;p&gt;Mặc d&amp;ugrave; nền kinh tế Singapore vẫn trong t&amp;igrave;nh trạng ảm đạm nhưng Charles &amp;amp; Keith đ&amp;atilde; kh&amp;ocirc;ng nản l&amp;ograve;ng, v&amp;agrave; nắm lấy cơ hội để ph&amp;aacute;t triển. Với định hướng mới, họ đ&amp;atilde; thu h&amp;uacute;t được sự ch&amp;uacute; &amp;yacute; của c&amp;aacute;c kh&amp;aacute;ch h&amp;agrave;ng nữ nữ, với kiểu d&amp;aacute;ng mới mẻ, dẫn đầu xu hướng thời trang v&amp;agrave; gi&amp;aacute; cả phải chăng - một điểm cộng trong thời điểm kinh tế suy tho&amp;aacute;i.&lt;/p&gt;\n\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;strong&gt;5. H&amp;atilde;y l&amp;agrave; đối thủ cạnh tranh lớn nhất của ch&amp;iacute;nh m&amp;igrave;nh&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;\n\n&lt;p&gt;Nhiều người kh&amp;ocirc;ng biết rằng Charles v&amp;agrave; Keith cũng sở hữu cả hệ thống cửa h&amp;agrave;ng gi&amp;agrave;y Pedro. Mặc d&amp;ugrave; ban đầu c&amp;aacute;c cửa h&amp;agrave;ng n&amp;agrave;y tập trung v&amp;agrave;o đối tượng kh&amp;aacute;ch h&amp;agrave;ng nam như một nỗ lực để tạo ra thương hiệu Charles &amp;amp; Keith của nam, nhưng sau đ&amp;oacute; Pedro đ&amp;atilde; dần mở rộng v&amp;agrave; chuyển hướng sang b&amp;aacute;n cả c&amp;aacute;c thiết kế d&amp;agrave;nh cho nữ. Điều n&amp;agrave;y đặt Pedro v&amp;agrave;o thế cạnh tranh trực tiếp với Charles &amp;amp; Keith, với hệ thống cửa h&amp;agrave;ng được đặt gần nhau.&lt;/p&gt;\n\n&lt;p&gt;Mặc d&amp;ugrave; mỗi thương hiệu c&amp;oacute; sự kh&amp;aacute;c biệt ri&amp;ecirc;ng (Pedro hướng đến phục vụ đối tượng kh&amp;aacute;ch h&amp;agrave;ng sang trọng hơn), nhưng cả hai thương hiệu đều th&amp;agrave;nh c&amp;ocirc;ng trong việc tạo sức h&amp;uacute;t tại thị trường địa phương. Đ&amp;oacute; l&amp;agrave; c&amp;aacute;ch tốt nhất để li&amp;ecirc;n tục thử th&amp;aacute;ch bản th&amp;acirc;n, trong khi vẫn ph&amp;aacute;t triển được c&amp;ocirc;ng việc kinh doanh chung.&lt;/p&gt;\n\n&lt;p&gt;Với Charles &amp;amp; Keith, nhu cầu của kh&amp;aacute;ch h&amp;agrave;ng c&amp;agrave;ng đa dạng th&amp;igrave; c&amp;ocirc;ng việc kinh doanh của họ lại c&amp;agrave;ng rộng mở.&lt;/p&gt;\n\n&lt;p&gt;&lt;span style=&quot;font-size:16px&quot;&gt;&lt;strong&gt;6. Nếu kh&amp;ocirc;ng tiến l&amp;ecirc;n ph&amp;iacute;a trước, bạn sẽ tụt l&amp;ugrave;i&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;\n\n&lt;p&gt;L&amp;agrave; một doanh nh&amp;acirc;n trong lĩnh vực thời trang, Charles biết r&amp;otilde; mọi thứ trong ng&amp;agrave;nh n&amp;agrave;y thay đổi nhanh ch&amp;oacute;ng như thế n&amp;agrave;o. Đ&amp;oacute; l&amp;agrave; l&amp;yacute; do tại sao đội ngũ đứng ph&amp;iacute;a sau thương hiệu phải kh&amp;ocirc;ng ngừng s&amp;aacute;ng tạo.&lt;/p&gt;\n\n&lt;p&gt;Bản th&amp;acirc;n Charles Wong cũng thường phải bay qua bay lại giữa Singapore v&amp;agrave; Thượng Hải - nơi đặt chi nh&amp;aacute;nh của Charles &amp;amp; Keith để quản l&amp;yacute; c&amp;ocirc;ng việc kinh doanh. C&amp;aacute;c nh&amp;agrave; thiết kế của h&amp;atilde;ng tại Trung Quốc cũng phải thường xuy&amp;ecirc;n phải đến ch&amp;acirc;u &amp;Acirc;u v&amp;agrave; Mỹ để tham dự c&amp;aacute;c show tr&amp;igrave;nh diễn thời trang v&amp;agrave; tiến h&amp;agrave;nh nghi&amp;ecirc;n cứu thị trường, chi ph&amp;iacute; đ&amp;agrave;o tạo c&amp;aacute;c nh&amp;agrave; thiết kế chiếm tới 3% lợi nhuận của c&amp;ocirc;ng ty.&lt;/p&gt;\n', '', '', '', '6-triet-ly-kinh-doanh-dua-den-thanh-cong-cua-ceo-charles-keith', 14, 1, 1, 0, '2015-10-15 04:18:23', '2015-11-02 03:42:04', 1, 0, 0),
(28, 'Sứ mệnh', '/public/images/Functional-foods-at-the-forefr-5600-7018-1387770506.jpg', '&lt;p&gt;&lt;span style=&quot;font-family:times new roman,times,serif&quot;&gt;&lt;span style=&quot;font-size:18px&quot;&gt;&lt;strong&gt;&amp;nbsp; &amp;nbsp;Sứ Mệnh:&lt;/strong&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\n', '&lt;table border=&quot;0&quot; cellpadding=&quot;0&quot; cellspacing=&quot;0&quot; style=&quot;width:500px&quot;&gt;\n	&lt;tbody&gt;\n		&lt;tr&gt;\n			&lt;td&gt;\n			&lt;p&gt;&amp;nbsp;&lt;/p&gt;\n\n			&lt;p&gt;o&amp;nbsp;&amp;nbsp;&amp;nbsp; K&amp;ecirc;́t n&amp;ocirc;́i v&amp;agrave; trao cho mọi người cơ h&amp;ocirc;̣i đạt được những mục ti&amp;ecirc;u của họ th&amp;ocirc;ng qua cơ h&amp;ocirc;̣i kinh doanh t&amp;ocirc;́i ưu v&amp;agrave; những d&amp;ograve;ng sản ph&amp;acirc;̉m ch&amp;acirc;́t lượng.&lt;/p&gt;\n\n			&lt;p&gt;o&amp;nbsp;&amp;nbsp;&amp;nbsp; Phụng sự đ&amp;ecirc;̉ th&amp;agrave;nh c&amp;ocirc;ng.&lt;/p&gt;\n			&lt;/td&gt;\n		&lt;/tr&gt;\n	&lt;/tbody&gt;\n&lt;/table&gt;\n\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\n', '', '', '', 'su-menh', 2, 0, 1, 0, '2015-10-17 03:23:10', '2015-10-25 03:59:52', 1, 0, 0),
(29, 'Chúc mừng ngày phụ nữ Việt Nam 20-10-2015', '/public/images/phu-nu-blogtamsuvn_jpg1_.jpg', '&lt;p&gt;Thư ch&amp;uacute;c mừng ng&amp;agrave;y phụ nữ Việt Nam của chủ tịch HĐQT&lt;/p&gt;\n', '&lt;p&gt;&lt;span style=&quot;color:#FF0000&quot;&gt;&lt;strong&gt;Th&amp;acirc;n gửi Quý nhà ph&amp;acirc;n ph&amp;ocirc;́i và Quý khách hàng nữ!&amp;nbsp;&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;\n\n&lt;p style=&quot;text-align:justify&quot;&gt;Th&amp;aacute;ng 10 về, đ&amp;acirc;u đ&amp;oacute; ở khắp mọi miền đất nước, mọi người đang n&amp;ocirc; nức tận hưởng bầu kh&amp;ocirc;ng kh&amp;iacute; tuyệt&amp;nbsp;vời của một ng&amp;agrave;y đặc biệt -&amp;nbsp;ng&amp;agrave;y 20/10 &amp;ndash; ng&amp;agrave;y t&amp;ocirc;n vinh phụ nữ Việt Nam. (Ng&amp;agrave;y Phụ nữ Vi&amp;ecirc;̣t Nam&amp;nbsp;20/10 h&amp;agrave;ng năm đ&amp;atilde; trở th&amp;agrave;nh ng&amp;agrave;y để ch&amp;uacute;ng ta t&amp;ocirc;n vinh c&amp;aacute;c b&amp;agrave;,&amp;nbsp;c&amp;aacute;c mẹ, c&amp;aacute;c chị, c&amp;aacute;c em g&amp;aacute;i &amp;ndash; những&amp;nbsp;người phụ nữ quan trọng trong cuộc sống của ch&amp;uacute;ng ta. Kh&amp;ocirc;ng ai c&amp;oacute; thể phủ nhận những hy&amp;nbsp;sinh,&amp;nbsp;đ&amp;oacute;ng g&amp;oacute;p lớn lao của những người phụ nữ: Trong kh&amp;aacute;ng chiến đấu tranh, họ l&amp;agrave; những B&amp;agrave; mẹ Việt&amp;nbsp;Nam anh h&amp;ugrave;ng, những nữ thanh ni&amp;ecirc;n xung phong ki&amp;ecirc;n cường, bất khuất; Trong thời b&amp;igrave;nh, họ l&amp;agrave;&amp;nbsp;những nữ doanh nh&amp;acirc;n th&amp;agrave;nh đạt, những nữ nh&amp;acirc;n vi&amp;ecirc;n, c&amp;ocirc;ng nh&amp;acirc;n&amp;nbsp;chăm chỉ; Trong gia đ&amp;igrave;nh, họ l&amp;agrave;&amp;nbsp;những người mẹ, người vợ đảm đang.)&lt;/p&gt;\n\n&lt;p style=&quot;text-align:justify&quot;&gt;Hòa chung trong kh&amp;ocirc;ng khí cả nước chào mừng ngày Phụ nữ Vi&amp;ecirc;̣t Nam 20/10, HĐQT C&amp;ocirc;ng ty C&amp;ocirc;̉ ph&amp;acirc;̀n&amp;nbsp;Đ&amp;acirc;̀u tư Sản xu&amp;acirc;́t và Thương mại&amp;nbsp;Sen Vi&amp;ecirc;̣t Group xin gửi tới quý nhà ph&amp;acirc;n ph&amp;ocirc;́i và Quý khách hàng nữ&amp;nbsp;những lời chúc tốt đẹp v&amp;agrave; ch&amp;acirc;n th&amp;agrave;nh nhất. Ch&amp;uacute;c cho c&amp;aacute;c Qu&amp;yacute;&amp;nbsp;b&amp;agrave;, Qu&amp;yacute; c&amp;ocirc; ng&amp;agrave;y c&amp;agrave;ng trẻ, đẹp, lu&amp;ocirc;n lu&amp;ocirc;n&amp;nbsp;mạnh khỏe, nhiều niềm vui, tr&amp;agrave;n đầy hạnh ph&amp;uacute;c v&amp;agrave; th&amp;agrave;nh c&amp;ocirc;ng hơn trong cuộc sống cũng&amp;nbsp;như trong c&amp;ocirc;ng&amp;nbsp;việc.&lt;/p&gt;\n\n&lt;p style=&quot;text-align:justify&quot;&gt;Cũng nh&amp;acirc;n dịp n&amp;agrave;y, C&amp;ocirc;ng ty C&amp;ocirc;̉ ph&amp;acirc;̀n Đ&amp;acirc;̀u tư Sản xu&amp;acirc;́t và Thương mại Sen Vi&amp;ecirc;̣t Group cũng xin gửi lời&amp;nbsp;cảm ơn ch&amp;acirc;n th&amp;agrave;nh v&amp;agrave; s&amp;acirc;u sắc nhất&amp;nbsp;tới sự tin tưởng, ủng hộ, quan t&amp;acirc;m v&amp;agrave; theo d&amp;otilde;i của Qu&amp;yacute; nhà ph&amp;acirc;n ph&amp;ocirc;́i,&amp;nbsp;và Quý kh&amp;aacute;ch hàng trong thời gian qua. C&amp;ocirc;ng ty C&amp;ocirc;ng ty C&amp;ocirc;̉&amp;nbsp;ph&amp;acirc;̀n Đ&amp;acirc;̀u tư Sản xu&amp;acirc;́t và Thương mại Sen&amp;nbsp;Vi&amp;ecirc;̣t Group lu&amp;ocirc;n mong muốn đ&amp;oacute;n nhận sự hợp t&amp;aacute;c, ủng hộ v&amp;agrave; đ&amp;oacute;ng g&amp;oacute;p để ng&amp;agrave;y c&amp;agrave;ng ph&amp;aacute;t&amp;nbsp;triển hơn.&lt;/p&gt;\n\n&lt;p style=&quot;text-align:center&quot;&gt;&lt;span style=&quot;font-size:14px&quot;&gt;&lt;strong&gt;&lt;em&gt;&amp;quot;Phụng sự đ&amp;ecirc;̉ thành c&amp;ocirc;ng!&amp;quot;&lt;/em&gt;&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;\n\n&lt;p style=&quot;margin-left: 240px; text-align: center;&quot;&gt;&lt;strong&gt;Xin ch&amp;acirc;n th&amp;agrave;nh cảm ơn sự quan t&amp;acirc;m của Q&amp;uacute;y kh&amp;aacute;ch h&amp;agrave;ng!&lt;/strong&gt;&lt;/p&gt;\n\n&lt;p style=&quot;margin-left: 240px; text-align: center;&quot;&gt;&lt;span style=&quot;font-size:14px&quot;&gt;&lt;strong&gt;Chủ tịch H&amp;ocirc;̣i đ&amp;ocirc;̀ng quản trị&amp;nbsp;&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;\n\n&lt;p style=&quot;margin-left: 240px; text-align: center;&quot;&gt;&lt;span style=&quot;font-size:14px&quot;&gt;&lt;strong&gt;Lương Qu&amp;acirc;n Đoàn&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;\n', '', '', '', 'chuc-mung-ngay-phu-nu-viet-nam-20-10-2015', 7, 0, 1, 0, '2015-10-21 12:56:16', '2015-10-21 12:58:32', 1, 0, 0),
(30, 'Quy định sử dụng', '/public/images/gioi%20thieu%20-%20ban%20lanh%20dao.jpg', '&lt;p&gt;Quy định sử dụng&lt;/p&gt;\r\n', '&lt;p&gt;Lorem ipsum dolor sit amet, consectetur adipiscing elit. Donec fringilla nisl id nulla varius placerat ac eu diam. Fusce ornare mi ut elit rhoncus tincidunt. Suspendisse semper, orci at convallis luctus, sem ex mattis sapien, sed lobortis elit quam sit amet turpis. Sed sit amet orci et ipsum placerat cursus. Vivamus quis mi porttitor, commodo lectus non, vulputate dui. Praesent porta aliquam dui, sed porta est tempor ut. Morbi lobortis, mi ac convallis imperdiet, eros nibh viverra dui, mollis dapibus turpis&lt;/p&gt;\r\n', '', '', '', 'quy-dinh-su-dung', 1, 0, 1, 0, '2015-10-26 15:59:40', '2015-10-26 16:06:38', 1, 0, 0),
(31, 'Thông báo về vấn đề gửi thông tin yêu cầu lên phòng IT', '/public/images/CustomerSupport.gif', '&lt;p&gt;Nhằm mục đ&amp;iacute;ch x&amp;acirc;y dựng quy tr&amp;igrave;nh l&amp;agrave;m việc hiện đại, gọn nhẹ v&amp;agrave; nhanh ch&amp;oacute;ng. Ph&amp;ograve;ng IT triển khai mẫu form y&amp;ecirc;u cầu online d&amp;agrave;nh cho c&amp;aacute;c tổ chức ph&amp;ograve;ng ban nội bộ trong c&amp;ocirc;ng ty. Mọi th&amp;ocirc;ng tin y&amp;ecirc;u cầu của c&amp;aacute;c ph&amp;ograve;ng đề nghị gửi l&amp;ecirc;n ph&amp;ograve;ng IT theo mẫu form n&amp;agrave;y.&lt;/p&gt;\n', '&lt;iframe src=&quot;https://docs.google.com/forms/d/1jOFI8ptKvFIT776iezuaEUK4PTx5sHrQbQOPH31dUAQ/viewform?embedded=true&quot; width=&quot;760&quot; height=&quot;800&quot; frameborder=&quot;0&quot; marginheight=&quot;0&quot; marginwidth=&quot;0&quot;&gt;Đang tải...&lt;/iframe&gt;', '', '', '', 'thong-bao-ve-van-de-gui-thong-tin-yeu-cau-len-phong-it', 7, 0, 1, 0, '2015-10-30 06:31:18', '2015-10-30 06:34:24', 1, 0, 0);

